import{_ as s,c as l,b as m,a,d as r,w as i,r as p,o,e as t}from"./app-CfGhvXUl.js";const u={},d={class:"table-of-contents"};function g(h,e){const n=p("router-link");return o(),l("div",null,[e[18]||(e[18]=m("h1",{id:"какого-же-маркса-мы-читаем",tabindex:"-1"},[m("a",{class:"header-anchor",href:"#какого-же-маркса-мы-читаем"},[m("span",null,"Какого же Маркса мы читаем?")])],-1)),e[19]||(e[19]=m("p",null,"Кондрашов Петр Николаевич. 2016.",-1)),m("nav",d,[m("ul",null,[m("li",null,[r(n,{to:"#часть-1"},{default:i(()=>[...e[0]||(e[0]=[t("Часть 1",-1)])]),_:1}),m("ul",null,[m("li",null,[r(n,{to:"#введение"},{default:i(()=>[...e[1]||(e[1]=[t("Введение",-1)])]),_:1})]),m("li",null,[r(n,{to:"#подлинная-и-неподлинная-деятельность"},{default:i(()=>[...e[2]||(e[2]=[t("Подлинная и неподлинная деятельность",-1)])]),_:1})]),m("li",null,[r(n,{to:"#das-produktiv-leben"},{default:i(()=>[...e[3]||(e[3]=[t("Das produktiv Leben",-1)])]),_:1})]),m("li",null,[r(n,{to:"#produktivkrafte-и-produktionkrafte"},{default:i(()=>[...e[4]||(e[4]=[t("Produktivkräfte и Produktionkräfte",-1)])]),_:1})]),m("li",null,[r(n,{to:"#die-arbeit"},{default:i(()=>[...e[5]||(e[5]=[t("Die Arbeit",-1)])]),_:1})]),m("li",null,[r(n,{to:"#aufhebung-der-arbeit"},{default:i(()=>[...e[6]||(e[6]=[t("Aufhebung der Arbeit",-1)])]),_:1})]),m("li",null,[r(n,{to:"#erzeugung-erzeugen-produktion"},{default:i(()=>[...e[7]||(e[7]=[t("Erzeugung, erzeugen, Produktion",-1)])]),_:1})]),m("li",null,[r(n,{to:"#библиография"},{default:i(()=>[...e[8]||(e[8]=[t("Библиография",-1)])]),_:1})])])]),m("li",null,[r(n,{to:"#часть-2"},{default:i(()=>[...e[9]||(e[9]=[t("Часть 2",-1)])]),_:1}),m("ul",null,[m("li",null,[r(n,{to:"#verkehr"},{default:i(()=>[...e[10]||(e[10]=[t("Verkehr",-1)])]),_:1})]),m("li",null,[r(n,{to:"#außerung-и-aneignung"},{default:i(()=>[...e[11]||(e[11]=[t("Äußerung и Aneignung",-1)])]),_:1})]),m("li",null,[r(n,{to:"#okonomie"},{default:i(()=>[...e[12]||(e[12]=[t("Ökonomie",-1)])]),_:1})]),m("li",null,[r(n,{to:"#dasensemble"},{default:i(()=>[...e[13]||(e[13]=[t("Dasensemble",-1)])]),_:1})]),m("li",null,[r(n,{to:"#versachlichung"},{default:i(()=>[...e[14]||(e[14]=[t("Versachlichung",-1)])]),_:1})]),m("li",null,[r(n,{to:"#menschlich"},{default:i(()=>[...e[15]||(e[15]=[t("Menschlich",-1)])]),_:1})]),m("li",null,[r(n,{to:"#выводы"},{default:i(()=>[...e[16]||(e[16]=[t("Выводы",-1)])]),_:1})]),m("li",null,[r(n,{to:"#библиография-1"},{default:i(()=>[...e[17]||(e[17]=[t("Библиография",-1)])]),_:1})])])])])]),e[20]||(e[20]=a('<h2 id="часть-1" tabindex="-1"><a class="header-anchor" href="#часть-1"><span>Часть 1</span></a></h2><p><strong>Аннотация.</strong> В статье показано, что в русскоязычных изданиях работ К. Маркса некоторые философские понятия (<em>produktiv Leben</em>, <em>Arbeit</em>, <em>Aufhebung der Arbeit</em>, <em>erzeugen</em>, <em>Erzeugung</em>) были переведены словами с производственно-экономическим смыслом, в то время как у самого Маркса они в различных контекстах несут в себе философско-антропологические и экзистенциальные коннотации. Такие переводы привели к радикальному извращению философии Маркса. Автор делает вывод о необходимости нового адекватного перевода трудов Маркса и о переосмыслении всей его философской системы.</p><p><strong>Ключевые слова:</strong> философия К. Маркса, философская терминология, перевод, семантические коннотации.</p><h3 id="введение" tabindex="-1"><a class="header-anchor" href="#введение"><span>Введение</span></a></h3><p>В последние годы структурного кризиса глобального капитализма наблюдается новый прилив интереса к антикапиталистическим теориям и практикам, но в первую очередь — к марксизму в целом и теоретическому наследию К. Маркса в частности. Этот интерес к Марксу наглядно обнаруживает себя как в росте переизданий его работ (в том числе и в России), так и в росте статей, книг и диссертаций (но, увы, не в России), посвященных анализу творческого наследия автора «Капитала», а также в проведении конференций и форумов, наиболее значимым из которых стал I Всемирный конгресс по марксизму, проходивший в октябре 2015 года в Пекине [3]. Добавим к этому и новые переводы работ немецкого мыслителя [6].</p><p>В рамках этого ренессанса Маркса (именно <em>Маркса</em>, а не Маркса-Энгельса и тем более не марксизма-ленинизма) имеют место действительно новаторские прочтения его философского, социологического и политического наследия, осуществляются, как и в середине XX в., продуктивные синтезы марксовой мысли с различными направлениями общественных и гуманитарных наук (экзистенциализм, феноменология, постмодернизм, лаканофрейдизм, аналитическая философия и даже ницшеанство). Идеи Маркса плодотворно применяются в истории, социологии, археологии, системном анализе, лингвистике, политологии, педагогике. Имя Маркса становится своеобразным брендом.</p><p>Однако при всем этом, вместе с интересными подходами к интерпретации философии Маркса и попытками выявить аутентичные взгляды Маркса, вновь возрождаются вульгарные, примитивные и совершенно превратные прочтения, редуцирующие идеи Маркса к некоему «рабоче-крестьянскому», «железобетонному» материализму, психологическому учению о материальных интересах и потребностях брюха как единственных мотивах деятельности людей во все исторические эпохи, к воинствующему атеизму и антигуманному тоталитаризму.</p><p>К сожалению, следует признать тот печальный факт, что, несмотря на давным-давно уже доказанную неадекватность сталинской интерпретации Маркса (которая существовала в СССР в виде догматического марксизма-ленинизма), именно она до сих пор продолжает существовать. Более того, на её основе пишутся соответствующие тексты, и — что самое неприятное — именно она в общественном мнении по сей день ассоциируется и отождествляется с <em>мыслью Маркса</em>. Хотя к последней она никакого отношения не имеет.</p><p>В связи с этим возникает вопрос: почему имеют хождение и популярность такие абсолютно неадекватные «прочтения» Маркса? Почему «после Иисуса Христа ни один человек не вызывал… всеобщего поклонения и в то же время не был настолько неправильно истолкован» [15, с. 5]? Поскольку мы не можем говорить о ситуации в глобальном масштабе («я вам не скажу за всю Одессу»), то, отбрасывая разного рода культурно-исторические, политические и идеологические причины хождения самых разнообразных мифов о философии Карла Маркса (анализ которых сегодня жизненно необходим), в <em>российской</em> ситуации можно выделить три основных причины, которые имеют несколько «лингвистическую» окраску.</p><p>Во-первых, даже те, кто гордо называет себя «марксистами», не говоря уже обо всех сочувствующих и примкнувших, или, напротив, отвергающих и критикующих, <em>самого Маркса просто-напросто не читают</em>. Не читали его, кстати говоря, и в «марксистские» времена ни в СССР, ни в странах социализма, ни на Западе. «Бесспорно, что «борьба с Марксом»… на самом деле была по большей части борьбой с ошибочной интерпретацией Маркса современными марксистами» [1, с. 15]. Как верно заметил Б. Оллман, «с момента зарождения марксизм подвергался атакам со всех сторон, но основная критика была направлена против утверждений, которые Маркс никогда не делал» [13].</p><p>Во-вторых, если о Марксе «что-то» и читали или читают, то это, как правило, либо тенденциозно скомпилированные хрестоматии из фрагментов сочинений Маркса-Энгельса-Ленина (которых представляют чем-то единым целым), либо старые учебники по истмату-диамату, в которых излагается сталинская версия ленинского прочтения, Плеханова и позднего Энгельса, в которой собственно Маркса-то и нет, либо, наконец, это современные учебники неолиберального толка, авторы которых излагают Маркса «по Попперу», который Маркса не понял, создав какую-то чудовищную карикатуру на него.</p><p>В-третьих, если кто-то уж очень дотошный из россиян и решит добраться до «самого Маркса» и откроет переводы его работ, чтобы их <em>действительно прочесть</em>, то и там он <em>самого Маркса может не найти</em>.</p><p>Оказывается, <em>нельзя доверять тем переводам текстов Маркса</em>, <em>которые имеются на сегодняшний день на русском языке</em>. Дело в том, что философия Маркса является по своему существу философско-антропологической, что обнаруживает себя не только в соответствующей терминологии, непосредственно используемой Марксом (<em>отчуждение</em>, <em>бесчеловечность</em>, <em>обесчеловечивание</em>, <em>страдание</em>), но и в <em>экзистенциальных</em> коннотациях вполне обычных терминов (<em>общение</em>, <em>труд</em> / <em>работа</em>, <em>ценность</em>, <em>потребление</em>), большинство из которых в советской версии марксизма было истолковано в <em>производственно-техническом смысле</em>, в то время как у самого основоположника они носят экзистенциальный характер. Однако эти экзистенциальные коннотации большей частью теряются при неверном переводе некоторых терминов, используемых Марксом, исчезают очень важные семантические оттенки слов, без учета которых адекватно понять мысль Маркса не представляется возможным. Экономически же ориентированные переводы, которые имеются ныне в русском варианте, по сути дела, <em>искажают мысль Маркса</em>, превращая его в «философа фабрики», «философа грубого коммунизма», сторонника всеобщей трудовой повинности, в то время как сам Маркс говорил о необходимости <em>Aufhebung der Arbeit</em>, <em>снятии труда</em>. Впрочем, здесь всё же следует отметить, что многие термины Маркса не имеют однозначного перевода и идеология здесь не причем. Об этом тоже не следует забывать.</p><p>В предлагаемой статье мы покажем то, каким образом кардинально извращается и даже обесчеловечивается философская мысль Маркса в переводах его философских терминов, которые у него выполняют <em>экзистенциальную</em> функцию, несут <em>жизненно-эмоциональные коннотации</em>, но в русских переводах, большей частью носящих экономико-производственный оттенок, эту функцию теряют и превращаются в сухие, безжизненные и бездушные технические термины. Все эти важнейшие философские коннотации настолько существенны для понимания подлинного Маркса, что за голову хватаешься, когда читаешь русские переводы. И в голове постоянно крутится вопрос: <em>так какого же Маркса мы читаем</em>?</p><h3 id="подлинная-и-неподлинная-деятельность" tabindex="-1"><a class="header-anchor" href="#подлинная-и-неподлинная-деятельность"><span>Подлинная и неподлинная деятельность</span></a></h3><p>К сожалению, в самых широких народных массах нашей страны философия Маркса понимается как некая «философия фабрики», «метафизика всеобщей трудовой повинности», в рамках которой действует категорический императив «кто не работает — тот не ест». Связано это, конечно же, с тем, что, во-первых, именно так интерпретированный марксизм преподавали в советской школе и преподают по сей день, поэтому <em>другого Маркса</em> люди просто-напросто <em>не знают</em>; во-вторых, основные свои работы Маркс посвятил социально-экономическому анализу капитализма, и производственный фактор у него, безусловно, часто выдвигается на передний план, особенно в его учении о <em>производительных</em> силах и <em>производственных</em> отношениях в структуре способа <em>производства</em>.</p><p>Но это относится к <em>политической экономии</em>. В русских же переводах этот <em>экономико-производственный</em> смысл был перенесен и в <em>философское</em> учение Маркса. Давайте возьмём конкретный пример. Не всем известно, но Маркс считал, что родовая сущность человека представляет собой не только «совокупность всех общественных отношений» (о которой будет сказано ниже), но <em>целеполагающую преобразующую материально-духовную деятельность</em> (<em>die Praxis</em>, <em>die Praktik</em>, <em>die Tätigkeit</em>), <em>труд</em> (<em>die Arbeit</em>). «Животное,— пишет Маркс,— непосредственно тождественно со своей жизнедеятельностью. Оно не отличает себя от своей жизнедеятельности. Оно есть <em>эта жизнедеятельность</em>. Человек же делает самоё свою жизнедеятельность предметом своей воли и своего сознания. Его жизнедеятельность — сознательная. Это не есть такая определенность, с которой он непосредственно сливается воедино. Сознательная жизнедеятельность непосредственно отличает человека от животной жизнедеятельности» [10, с. 565].</p><p>Итак, именно «свободная сознательная деятельность как раз и составляет родовой характер человека» [10, с. 565]. <em>Свободная</em>, по Марксу, означает: не детерминируемая экономической или природной необходимостью, не направленная на «работу ради выживания», а вызванная внутренним желанием её (такую деятельность) осуществлять, и получать от этого осуществления удовольствие. Маркс называет её <em>самодеятельностью</em>, <em>самоосуществлением</em> (<em>die Selbstbetätigung</em>) [11, с. 67–69]. Если несвободная, отчуждённая деятельность осуществляется человеком в силу того, что он (раб, зависимый крестьянин, рабочий) <em>должен</em> выполнять её, чтобы просто-напросто физически <em>выжить</em>, ибо у него нет иного источника для существования, кроме своей рабочей силы, то свободная деятельность, напротив, осуществляется человеком только потому, что он сам <em>хочет</em> её выполнять, даже несмотря на все присущие ей трудности (<em>муки творчества</em>). «Отчуждённый труд,— пишет Маркс,— переворачивает это отношение таким образом, что человек именно потому, что он есть существо сознательное, превращает свою жизнедеятельность [Lebenstätigkeit], свою <em>сущность</em> [sein <em>Wesen</em>] только лишь в средство для поддержания своего <em>существования</em> [seine <em>Existenz</em>]» [10, с. 566].</p><p>Более того, <em>die Selbstbetätigung</em> как раз-то и представляет собою <em>подлинную человеческую активность</em>, в противоположность неподлинной, отчуждённой. Эта <em>подлинная</em>, <em>«истинно человеческая жизнь»</em> (<em>wahrhaft menschliche Leben</em>) [10, с. 637], часто сравнивается Марксом с <em>любовью</em>, в которой имеет место <em>имманентное единство</em>, <em>согласие</em>, <em>рефлексия-одного-в-другое</em>, когда человек по отношению <em>к другому</em> испытывает <em>симпатическую неравнодушность</em>: «Существование того, что я действительно люблю, я ощущаю как необходимое, я чувствую в нем потребность, без него мое существование не может быть полным, удовлетворенным, совершенным» [4, с. 35–36].</p><p>Подлинное человеческое бытие означает реализацию всех <em>проявлений жизни</em> (<em>Lebensäußerung</em>), возможность <em>самореализации</em> (<em>Selbsterschaffung</em>) [10, с. 624]. Именно такое состояние Маркс характеризует как <em>человечность</em> (<em>Menschlichkeit</em>), <em>гуманизм</em> [10, с. 588, 590, 637]. Если же рассматривать проблему в самом широком социальном плане, то люди, живущие в условиях <em>человечного общества</em> (<em>коммунизма</em>), реализуя свою родовую человеческую сущность — праксис — будут получать именно такое взаимное удовольствие. Подобная форма праксиса, человеческого бытия-в-мире, <em>Selbstbetätigung</em>, связана со <em>свободой</em>, которая и есть основа человечности, в рамках которой нет неестественного принуждения [12, с. 187], отчуждения, равнодушности, враждебной разобщенности, а есть <em>свободное проявление человеческих склонностей</em> (<em>Äußerung der menschlichen Triebe</em>) [12, с. 187], полное проявление сущностных сил человека, искренний человеческий восторг (<em>menschliche Freude</em>), <em>богатство ощущений</em> (<em>Reichtum der Empfindung</em>) и <em>бьющая ключом радость жизни</em> (<em>sprudelt Lebenslust</em>) [12, с. 186].</p><h3 id="das-produktiv-leben" tabindex="-1"><a class="header-anchor" href="#das-produktiv-leben"><span>Das produktiv Leben</span></a></h3><p>Отметим очень важный акцент, который делает Маркс, и на который позже обратит пристальное внимание Э. Фромм: сущностной характеристикой свободной деятельности является её <em>продуктивный</em> характер. Маркс <em>буквально</em> пишет: «Das <strong>produktive</strong> Leben ist aber das Gattungsleben. Es ist das Leben erzeugende Leben» [19, s. 516], что в <em>буквальном</em> переводе означает: «<strong>Продуктивная</strong> жизнь и есть родовая жизнь. Это есть жизнь, порождающая жизнь». Однако в первом издании «Экономическо-философских рукописей 1844 года» (1956) мы читаем: «<strong>производственная</strong> жизнь и есть родовая жизнь. Это есть жизнь, порождающая жизнь» [10, с. 565]. Заметьте, какая произошла радикальная инверсия и подмена: <em>продуктивная</em> жизнь превратилась в жизнь <em>производственную</em>, т. е. в жизнь заводскую, фабричную, колхозно-совхозную.</p><p>Только слепой не может здесь увидеть именно <em>экономическое</em> истолкование и перевод словосочетания «das <strong>produktive</strong> Leben». Действительно, как верно в другом отношении заметила Вальтраут Шелике, подобным переводом советские толмачи просто <em>обесчеловечили мысль Маркса</em>. Представьте себе хотя бы на минуту, что ВОТ ЭТО (что родовая жизнь человека — это жизнь <strong>производственная</strong>) прочитал школьник или студент первого курса, поверхностно изучающий философию, и который не видел немецкого оригинала: что он подумает о Марксе, человеке, который родовую жизнь человека редуцировал к <em>производству на фабрике</em>, свёл к <em>рабоче-крестьянскому труду</em>? Какое мнение у него сложится о философии Маркса? Да самое ужасное: он будет считать, что с точки зрения Маркса, человек — это <em>производственное животное</em>, существо, предназначенное горбатиться на производстве, на фабриках и заводах… И при этом преподаватель философии будет тыкать в перевод: это, мол, Маркс сам так написал. При этом ни тот, ни другой никогда не откроют оригинал и не посмотрят, а что же Маркс там <em>на самом деле написал</em>.</p><p>Только на этом примере мы видим, как <em>всего лишь одно неверно переведённое слово радикально изменяет всю философию Маркса на противоположное учение</em>. Причём для <em>советских</em> идеологов был релевантен именно <em>такой</em> перевод: «производственная» жизнь как сущность человека — это как раз чистой воды идеологическое оправдание сталинской индустриализации, разного рода производственных свершений и т. д., но прежде всего — оправдание рабства, имевшего место в сталинском Союзе. Оправдание, освящённое именем самого Маркса…</p><h3 id="produktivkrafte-и-produktionkrafte" tabindex="-1"><a class="header-anchor" href="#produktivkrafte-и-produktionkrafte"><span>Produktivkräfte и Produktionkräfte</span></a></h3><p>В этом же контексте следует обратить внимание и на утерянное в русском переводе различение Марксом и Энгельсом таких важнейших понятий, как <em>Produktivkräfte</em> и <em>Produktionkräfte</em>, которые были переведены одним термином <em>производительные силы</em>. Вальтраут Шелике рассматривает эти понятия как результат пытки основоположников выявить диалектику <em>потенциальных</em> и <em>действительных</em> производительных сил. По её мнению, <em>Produktivkräfte</em> следует переводить как <em>производительные</em> силы, в то время как <em>Produktionkräfte</em> — силы <em>производственные</em>, ибо <em>Produktivkräfte</em> «вбирают в себя всю совокупность тех потенциальных возможных сил развития, которые уже аккумулированы в потребностях действительных индивидов, в их средствах производства, средствах общения и т. д., но еще не включены в производительную деятельность людей… А вот производственные силы (<em>Produktionkräfte</em>) это такие действительные производительные силы (<em>Produktivkräfte</em>), которые уже утвердились в действительности в процессе продуктивной деятельности индивидов и воплотились в действительных продуктах — материальных и духовных, природных и общественных, потребительских и меновых и т. д.» [16, с. 43–44].</p><p>В. Шелике подчёркивает, что у Маркса и Энгельса речь идёт именно о действительных и действенных <em>силах</em> действительных живых индивидов в структуре их деятельности. Стало быть, <em>силы</em> эти обнаруживают себя с одной стороны в виде природных и общественных свойств и качеств людей (в рукописях 1844 года Маркс употреблял понятие <em>сущностные силы человека</em>, <em>menschlichen Wesenskräfte</em> [10, с. 593–596, 599, 608, 614. 615, 618, 619–620, 626, 628, 630–631, 632, 637, 638]), «сводящихся к способности производить людей, предметы и отношения в процессе продуктивной деятельности» [16, с. 44], а с другой — в виде конкретных результатов действительной продуктивной деятельности действительных индивидов,— в виде конкретных людей, предметов и отношений.</p><p>Но в русском переводе такое различение этих двух форм человеческих сил элиминируется, исчезает. Более того, это неправомерное сведе́ние <em>Produktivkräfte</em> к <em>Produktionkräfte</em>, т. е. к вещной форме наличного бытия человеческих <em>созидательных</em> сил упускает из вида <em>самих людей как созидателей</em>, как <em>homines creatores</em>. Это ведёт не только к «техническому» редукционизму в интерпретации материалистического понимания истории, но и к вещному фетишизму, когда за вещами и продуктами человеческой деятельности не видят самого действующего человека. «На самом деле,— совершенно справедливо заключает В. Шелике,— производительные силы лишь аккумулированы в вещах и проявляются в вещах только в результате процесса взаимодействия субъекта деятельности (человека) с предметом деятельности (природного или общественного) посредством средств деятельности» [16, с. 44].</p><h3 id="die-arbeit" tabindex="-1"><a class="header-anchor" href="#die-arbeit"><span>Die Arbeit</span></a></h3><p>Однако двинемся дальше. Коль скоро Маркс определяет сущность человека через продуктивную деятельность, через <em>труд</em> в самом широком смысле этого слова, то тогда обратимся именно к этому термину. Трудность адекватного перевода немецкого <em>die Arbeit</em> состоит в том, что в русском языке оно передаётся двумя словами: и как <em>работа</em>, и как <em>труд</em>, которые имеют <em>различную</em> оценочную модальность: под <em>трудом</em> обычно понимают человеческую деятельность вообще, а под <em>работой</em>, как правило, конкретную трудовую деятельность, причём с негативными коннотациями («работа не волк» и т. п.). В силу же того, что у Маркса мы наблюдаем подобную семантическую двойственность при употреблении слова <em>die Arbeit</em> (в одном случае о <em>«die Arbeit»</em> Маркс говорит как о <em>труде вообще</em>, т. е. родовой характеристике человека (<em>die Praxis</em>, <em>die Tätigkeit</em>), а в другом — как о чём-то <em>тяжёлом</em>, <em>нудном</em>, <em>утомительном</em> и <em>обесчеловечивающем</em>, иногда Маркс употребляет в этих контекстах английское <em>toil</em> — <em>тяжёлый, непосильный труд</em>), то, представляется, что при переводе конкретного словоупотребления надо учитывать тот <em>контекст</em>, в котором этот термин стоит в текстах Маркса.</p><p>Маркс родовую сущность, помимо <em>die Arbeit-труд</em>, именует такими терминами, как <em>die Praxis</em>, <em>die Praktik</em>, <em>die Thätigkeit</em> (как в такой устаревшей форме, так и в современной <em>Tätigkeit</em>), различая внутри неё <em>деятельность подлинную</em> (<em>die Selbstbetätigung</em>) и <em>деятельность неподлинную</em> (<em>die entfremdete Arbeit</em>). И в этом плане надо чутко улавливать эти важнейшие семантические коннотации понятия <em>die Arbeit</em> в текстах Маркса: если речь идёт о деятельности, праксисе как о <em>родовой сущности человека</em>, то тогда <em>die Arbeit</em> следует переводить как <em>труд</em>; если же речь идёт о <em>неприятной, вынужденной</em>, <em>отчуждённой форме деятельности</em>, то тогда лучше эту экзистенциальную коннотацию передать русским словом <em>работа</em>.</p><p>Известный исследователь проблем человеческой деятельности В. В. Парцвания пишет: «Действительное содержание понятий «работа» и «труд» искажено и отождествлено. Заметим, что из-за лингвистического аспекта (проблем, связанных с неточным переводом и отсутствием в немецком языке соответствующих терминов), о котором говорилось выше, неточно толкуются постулаты Маркса по проблемам феномена труда… Деятельность человека, которая для одних создает богатства, а других ведёт к обнищанию, не может быть действительной деятельностью, то есть трудом. Такой процесс именуется <em>работой</em>. Такого рода человеческая деятельность противоречива и наносит ущерб, непосредственно рабочим, которые лишены счастья быть людьми (человеком)» [14, гл. III, § 3.2].</p><p>Достаточно сравнить приведённые ниже четыре фрагмента, взятые из разных периодов творчества Маркса, чтобы понять, что понятие <em>die Arbeit</em> употребляется в них с различной семантикой, и если мы без соответствующих контекстуальных пояснений будем его переводить как <em>труд</em> или <em>работа</em>, взятых в качестве <em>синонимов</em>, то ускользнёт важнейшее в мысли Маркса.</p><table><thead><tr><th>Имеющийся перевод</th><th>Контекстуальный перевод</th></tr></thead><tbody><tr><td>«Производство человеческой деятельности как <em><strong>труда</strong></em>, т. е. деятельности совершенно чуждой себе, человеку и природе, и потому совершенно чуждой сознанию и жизненному проявлению, <em>абстрактное</em> существование человека исключительно лишь как <em><strong>человека труда</strong></em>, который поэтому ежедневно может скатиться из своего заполненного ничто в абсолютное ничто, в свое общественное и потому действительное небытие» [10, с. 575].</td><td>«Производство человеческой деятельности как <em><strong>работы</strong></em> [menschlichen Tätigkeit als <em><strong>Arbeit</strong></em>], т. е. деятельности совершенно чуждой себе, человеку и природе, и потому совершенно чуждой сознанию и жизненному проявлению, <em>абстрактное</em> существование человека исключительно лишь как <em><strong>работяги</strong></em> [<em><strong>Arbeitsmenschen</strong></em>], который поэтому ежедневно может скатиться из своего заполненного ничто в абсолютное ничто, в свое общественное и потому действительное небытие».</td></tr><tr><td>«Процесс <strong>труда</strong>… в простых и абстрактных его моментах, есть целесообразная деятельность для созидания потребительных стоимостей, присвоение данного природой для человеческих потребностей, всеобщее условие обмена веществ между человеком и природой, вечное естественное условие человеческой жизни, и потому он не зависим от какой бы то ни было формы этой жизни, а, напротив, одинаково общ всем её формам» [5, с. 195].</td><td>Перевод правильный, ибо здесь речь идёт <em><strong>die Arbeit-труде</strong></em> <em>как</em> родовой характеристике человека (<em>die Praxis</em>, <em>die Tätigkeit</em>)</td></tr><tr><td>«Только на этой ступени самодеятельность (<em>Selbstbetätigung</em>) совпадает с материальной жизнью, что соответствует развитию индивидов в целостных индивидов и устранению всякой стихийности. Точно так же соответствуют друг другу превращение <strong>труда</strong> в самодеятельность (<em>die Verwandlung der <strong>Arbeit</strong> in Selbstbetätigung</em>) и превращение прежнего вынужденного общения в такое общение, в котором участвуют индивиды как таковые» [11, с. 68–69].</td><td>Перевод неправильный, ибо здесь речь идёт <em><strong>die Arbeit-работе</strong></em> <em>как обесчеловечивающей отчуждённой форме деятельности</em>, что прямо видно на противопоставления Марксом понятий <em>die Arbeit</em> и <em>die Selbstbetätigung</em>. Стало быть, учитывая и особый перевод <em>Selbstbetätigung</em>, мысль Маркса следует по-русски так: «Только на этой ступени <em>свободное самоосуществление</em> (<em>Selbstbetätigung</em>) совпадает с материальной жизнью, что соответствует развитию индивидов в целостных индивидов и устранению всякой стихийности. Точно так же соответствуют друг другу <em>превращение <strong>работы, отчуждённой формы труда</strong> в свободное самоосуществление</em> (<em>die Verwandlung der <strong>Arbeit</strong> in Selbstbetätigung</em>) и превращение прежнего вынужденного общения в такое общение, в котором участвуют индивиды как таковые».</td></tr><tr><td>«<em>Разделение труда</em> делает возможным — более того: действительным,— что духовная и материальная деятельность, наслаждение и труд, производство и потребление выпадают на долю различных индивидов; добиться того, чтобы они не вступали друг с другом в противоречие, возможно только путём устранения разделения труда» [11, с. 30–31].</td><td>Здесь очень трудно перевести посредством разделения понятий «работа» и «труд»,— только в противопоставлении <em>наслаждения</em> и <em>Arbeit</em> последнее лучше перевести как <em><strong>работа</strong></em>, в то время как в словосочетании <em>разделение труда</em> необходимо оставить <em><strong>труд</strong></em>, ибо речь идёт о человеческом праксисе как тотальности, единстве физической и духовной активности.</td></tr></tbody></table><p>Глубинная пассивность неподлинного способа бытия, по Марксу, фундирована в том, что деятельность в капиталистическом обществе (Arbeit-<em>работа</em>) характеризуется «однообразием, бессодержательностью и подчинением машине» [8, с. 511], это — «мертвящий труд» [8, с. 511], основная черта которого «состоит в <em>пассивном подчинении</em> рабочего движению самого механизма, в полном приспособлении рабочего к потребностям и требованиям этого механизма» [8, с. 504]. «Все эти действия, выполняемые занятым у машины рабочим, примечательны своей <em>пассивностью</em>, своей приспособленностью к операциям и движениям самой машины, подчинением ей. Эта <em>специализация пассивности</em>, т. е. уничтожение самой специализации как таковой, является характерной чертой машинного труда» [8, с. 511]. Именно пассивность этой повседневной, рутинной деятельности и конституирует экзистенциальную пассивность вообще. «Здесь отпадает последняя самоудовлетворенность рабочего своим трудом, здесь имеет место абсолютное безразличие, обусловленное самой бессодержательностью этого труда» [8, с. 511]. В своих работах Маркс и Энгельс показывают «возмутительное, бессердечное отношение промышленной буржуазии к рабочим, показывает всю подлую систему промышленной эксплуатации во всей ее бесчеловечности» [18, с. 399] и «сотне других гнусностей и мерзостей» [17, с. 395]. <em>Деятельность</em> же, совершающаяся в условиях, в которых уничтожен отчуждённый труд, по сути дела, совпадает с <em>наслаждением</em>, ибо в ней «отпадает самая основа всей этой противоположности между трудом и наслаждением!» [11, с. 206].</p><h3 id="aufhebung-der-arbeit" tabindex="-1"><a class="header-anchor" href="#aufhebung-der-arbeit"><span>Aufhebung der Arbeit</span></a></h3><p>Именно в этом контексте надо понимать и известные положения Маркса о революции и уничтожении труда, ибо «существование страдающего человечества, которое мыслит, и мыслящего человечества, которое подвергается угнетению, должно неизбежно стать поперек горла пассивному, бессмысленно наслаждающемуся животному миру филистерства» [7, с. 378], и поэтому коммунистическая революция сбросит «с себя всю старую мерзость» (<em>ganzen alten Dreck</em>) [11, с. 70] и уничтожит «современную форму деятельности [die moder [ne] Form der Tätigkeit]» [11, с. 70], т. е. <em>Arbeit-работу</em>: «при всех прошлых революциях характер деятельности всегда оставался нетронутым,— всегда дело шло только об ином распределении этой деятельности, о новом распределении труда между иными лицами, тогда как коммунистическая революция выступает против прежнего <em>характера</em> деятельности, устраняет <em>труд</em>» [11, с. 70]. Это <em>освобождение</em> (Befreiung) означает <em>упразднение</em>, <em>снятие труда-работы</em> (<em>Aufhebung der Arbeit</em>) [11, с. 70, 207] и превращение человеческого бытия в <em>наслаждение от осуществления своей собственной сущности — свободной продуктивной деятельности</em> (<em>Selbstbetätigung</em>) [11, с. 69].</p><p>Ранее вообще мало кто (даже среди марксистов) понимал это туманное место из «Немецкой идеологии», в котором Маркс и Энгельс говорят об <em>Aufhebung der Arbeit</em>. Затруднение это вызвано ещё и совершенно чудовищным переводом <em>Aufhebung</em> через русское <em>уничтожение</em>. Думается, что после проведённого анализа это становится совершенно прозрачным: под <em>трудом</em> здесь Маркс и Энгельс понимают не <em>деятельность вообще</em> (<em>Praxis</em>, <em>die Tätigkeit</em>), ибо таковая может исчезнуть только вместе с человеком, а несвободную, вынужденную деятельность, <em>работу</em>, приносящую страдания (<em>Arbeit-работу</em>). Непонимание того, что для Маркса <em>«Aufhebung der Arbeit»</em> означает именно преодоление отчуждённой формы деятельности — <em>работы</em> (<em>die Arbeit</em>), а не вообще <em>продуктивной деятельности</em> (<em>Praxis</em>, <em>die Tätigkeit</em>), приводит к непониманию и учения Маркса о будущей, постэкономической формации, ибо она как раз-то, с его точки зрения, и будет характеризоваться отсутствием работы как отчуждённой деятельности. И именно <em>свободная продуктивная деятельность</em> (<em>Selbstbetätigung</em>), а не «работа» превратится в будущем <em>во внутреннюю потребность человека</em>.</p><p>Вот что пишет И. Джохадзе, критикуя П. Козловски и Ф. Фукуяму, которые рассматривают <em>Aufhebung der Arbeit</em> именно как <em>Aufhebung der Tätigkeit</em>, и приписывают свою интерпретацию Марксу: «Эти и подобные им критические рассуждения современных авторов-«антиутопистов» никак не затрагивают аргументации теоретиков отмены труда (от Аристотеля до Жака Эллюля), поскольку в понятия, которыми оперируют обе стороны, вкладываются совершенно различные смыслы. В одном случае под трудом понимают жизнедеятельность человека <em>вообще</em>, безотносительно к конкретно-историческим и экзистенциальным обстоятельствам; в другом случае имеют в виду <em>специфическую форму</em> труда, преобладающую в панлейбористском обществе,— труд наемных работников и профессиональных служащих. Aufhebung отменяет только наёмный труд — ту разновидность активности человека, которая продиктована «нуждой и внешней целесообразностью» (Маркс) не в меньшей степени, чем добуржуазное (кажущееся теперь невозможным) рабство. Но лишить человека труда в первом из противопоставленных смыслов — трудовой деятельности <em>как таковой</em>, предполагающей, по Козловски, «серьезное, планомерное и связанное с чувством ответственности столкновение» с миром и с окружающими людьми,— значит лишить его самой жизни. В этом широком смысле созидательно-трудовая активность (действительно, не имеющая ничего общего с «чистой свободой выбора и произволом поведения») составляет сущность существования-человека-в-мире. Задача заключается как раз в том, чтобы <em>обеспечить возможность экзистенциальной активности человека</em>, его неотчужденной осмысленной жизнедеятельности,— и отмена внешнего (принудительного) труда является непременным условием этого. За пределом репрессивного мира наемного рабства депрофессионализированный индивид, избавившись от экономической и психологической зависимости от конкретной профессиональной деятельности, сможет обрести в свободном труде подлинный смысл своего существования. Вместо того чтобы трудиться для жизни, он получит возможность жить для труда» [2, с. 16–17]. Таким образом, можно согласиться с Б. Шоу, который заметил: «труд по обязанности — это работа, а работа по склонности — досуг».</p><h3 id="erzeugung-erzeugen-produktion" tabindex="-1"><a class="header-anchor" href="#erzeugung-erzeugen-produktion"><span>Erzeugung, erzeugen, Produktion</span></a></h3><p>При анализе текстов Маркса надо всегда иметь в виду, что экономические категории носят у него не только производственный, технический или маркетинговый характер, но всегда содержат в себе экзистенциальные коннотации, которые можно понять только в связи с их укоренённостью в его работах 40-х гг. Так, например, в понятии <em>Erzeugung</em> Маркс мыслит не только <em>производство</em> в смысле <em>делания</em> или, как позже Хайдеггер, <em>про-изведение</em>, но в первую очередь диалектику процессов <em>Äußerung</em> (опредмечивания сознания в производимую вещь) и <em>Aneignung</em> (распредмечивания опредмеченного сознания в практическом освоении предмета). Поэтому-то в «Капитале» Маркс рассуждает о <em>товаре</em> как о <em>чувственно-сверхчувственной предметности</em> [5, с. 80–81], поэтому он говорит, что в условиях отчуждения в процессе <em>Erzeugung</em> «человек вкладывает живую душу в продукт труда, теряя при этом свою собственную» [9, с. 450]. Более того, употребляемое Марксом слово <em>Zeugung</em>, идущее от <em>erzeugen</em> в значении <em>зачать, рождать</em>, у нас перевели как <em>«половой акт»</em> [10, с. 564].</p><p>В силу наличия таких коннотаций слово <em>Erzeugung</em> лучше перевести как <em>созидание</em>, а не <em>производство</em>, ибо в <em>производстве</em> слышится больше технической семантики, передаваемой словом <em>Produktion</em>. А ведь именно в силу этого технического перевода глагола <em>erzeugen</em> как <em>производить</em>, а не <em>созидать</em>, <em>созидательная деятельность человека вообще</em> была редуцирована к одной из своих форм — <em>деятельности производственной</em>. Причём такой редукцией сильно гордились марксисты-позитивисты: наконец-то, мол, можно чисто эмпирически фиксировать в экономических, производственных механизмах и отношениях всё содержание «человеческого». Но у Маркса этого <em>нет</em>: «Подчеркнем,— пишет В. Ф. Шелике,— что Маркс и Энгельс выбрали для исходного определения социальной деятельности именно <em>erzeugen</em>, а не <em>produzieren</em>, и это не случайно. Из исходных определений Маркса и Энгельса трёх сторон социальной деятельности напрашивается вывод, что потребность и способность человечества к созиданию по существу есть родовая сущность человека (человечества)» [17, с. 98].</p><h3 id="библиография" tabindex="-1"><a class="header-anchor" href="#библиография"><span>Библиография</span></a></h3><ol><li><p>Бергер П., Лукман Т. Социальное конструирование реальности: Трактат по социологии знания.— М.: Медиум, 1995.— 323 с.</p></li><li><p>Джохадзе И. Homo faber и будущее труда // Логос. № 6 (45). 2004. С. 3–17.</p></li><li><p>Кондрашов П. Н. Первый Всемирный конгресс по марксизму в Пекине: глобальное будущее // Дискурс-Пи, 2015, № 3–4 (20–21). С. 134–143.</p></li><li><p>Маркс К. Дебаты о свободе печати // К. Маркс, Ф. Энгельс. Соч., 2-е изд.— М.: Политиздат, 1955. Т. 1. С. 30– 84.</p></li><li><p>Маркс К. Капитал I // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1960. Т. 23. С. 5–784.</p></li><li><p>Маркс К. Капитал. Критика политической экономии. Т. 1. Кн. 1. Процесс производства капитала / под ред. В. Я. Чеховского.— М.: Политическая энциклопедия, 2015.— 661 с.</p></li><li><p>Маркс К. Письма из «Deutsch-Französische Jahrbücher». Письмо Маркса к А. Руге, май 1843 г. // Маркс К., Энгельс Ф. Сочинения. М.: Политиздат, 1955. Т. 1. С. 372–378.</p></li><li><p>Маркс К. Экономическая рукопись 1861–1863 гг. / К. Маркс // Маркс К., Энгельс Ф. Соч. 2-е изд.— М.: Политиздат, 1973.— Т. 47.— С. 3–612.</p></li><li><p>Маркс К. Экономические рукописи 1857–1859 гг. // Маркс К., Энгельс Ф. Соч. 2-е изд.— М.: Политиздат, 1968. Т. 46. Ч. I. С. 3–508.</p></li><li><p>Маркс К. Экономическо-философские рукописи 1844 г. // Маркс К., Энгельс Ф. Из ранних произведений.— М.: Политиздат, 1956. С. 517–642.</p></li><li><p>Маркс К., Энгельс Ф. Немецкая идеология // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1955. Т. 3. С. 9–544.</p></li><li><p>Маркс К., Энгельс Ф. Святое семейство // Маркс К., Энгельс Ф. Соч., 2-е изд. М.: Политиздат, 1955. Т. 2. С. 3–230.</p></li><li><p>Оллман Б. Что такое марксизм? Взгляд с высоты птичьего полета // www.alternativy.ru/ru/socialism21/ollman/ marxism.</p></li><li><p>Парцвания В. В. Генеалогия отчуждения: от человека абстрактного к человеку конкретному.— СПб., 2003.— 371 с.</p></li><li><p>Уин Ф. Карл Маркс.— М.: ООО «Издательство АСТ», 2003.— 428 с.</p></li><li><p>Шелике В. Ф. Методология определения К. Марксом и Ф. Энгельсом производительных сил в работе «Немецкая идеология» // К. Маркс и Ф. Энгельс о вопросах социальной диалектики.— Фрунзе: Изд-во КГУ, 1983. С. 39– 52.</p></li><li><p>Шелике В. Ф. Непознанный Маркс и некоторые проблемы современности (Часть 2) // Философские науки. 2013. № 4. С. 94–107.</p></li><li><p>Энгельс Ф. Положение рабочего класса в Англии // Маркс К., Энгельс Ф. Соч., 2-е изд. М.: Политиздат, 1955. Т. 2. С. 231–517.</p></li><li><p>Marx K. Ökonomisch-philosophischen Manuskripten aus dem Jahre 1844 // Marx K., Engels F. Werke. Bd. 40.— Berlin: Dietz Verlag, 1968. S. 465–588.</p></li></ol><h2 id="часть-2" tabindex="-1"><a class="header-anchor" href="#часть-2"><span>Часть 2</span></a></h2><p><strong>Аннотация.</strong> В статье показано, что в русскоязычных изданиях работ К. Маркса некоторые философские понятия (menschlich, Versachlichung, ensemble, Ökonomie, Äußerung, Aneignung, Verkehr) были переведены словами с производственно-экономическим смыслом, в то время как у самого Маркса они в различных контекстах несут в себе философско-антропологические и экзистенциальные коннотации. Такие переводы привели к радикальному извращению философии Маркса. Автор делает вывод о необходимости нового адекватного перевода трудов Маркса и о переосмыслении всей его философской системы.</p><p><strong>Ключевые слова:</strong> философия К. Маркса, философская терминология, перевод, семантические коннотации.</p><p>В предыдущем разыскании были рассмотрены неточности переводов таких важнейших понятий философии К. Маркса, как <em>Arbeit, Praxis, Selbstbetätigung, produktiv Leben, Produktivkräfte, Produktionkräfte, Aufhebung der Arbeit, Erzeugung, erzeugen, Produktion</em>. Здесь мы рассмотрим понятия <em>menschlich, Versachlichung, ensemble, Ökonomie, Äußerung, Aneignung, Verkehr</em>.</p><h3 id="verkehr" tabindex="-1"><a class="header-anchor" href="#verkehr"><span>Verkehr</span></a></h3><p>Одной из базовых категорий философии Маркса и становящегося материалистического понимания истории является понятие <em>Verkehr</em>. Однако стоит только посмотреть на русские переводы этого термина, как становится ясным: мы читаем не самого́ Маркса, а что-то к нему имеющее весьма отдалённое отношение. В. Ф. Шелике справедливо указывает, что это понятие, означающее <em>общение в самом широком</em> смысле, в собрании сочинений обычно переводят как <em>обмен</em>, связывая его только с <em>производственными отношениями</em>, упуская при этом радикально широкие семантические поля этого немецкого слова [13]. Приведём пару примеров такого перевода.</p><p>Уже в знаменитом письме П. В. Анненкову (1846) Маркс говорит о многозначности немецкого слова <em>Verkehr</em>, сравнивая его с французским <em>commerce</em>, но сам использует эти слова ещё в очень узком смысле, который радикально расширен им уже в «Немецкой идеологии» (1846–1847) [6].</p><p>В «Манифесте» (1848) Маркс и Энгельс пишут: «Wir sehen also, wie die moderne Bourgeoisie selbst das Produkt eines langen Entwicklungsganges, einer Reihe von Umwälzungen in der Produktions- und <strong>Verkehrsweise</strong> ist» [22, s. 464], у нас же переводят: «Мы видим, таким образом, что современная буржуазия сама является продуктом длительного процесса развития, ряда переворотов в <strong>способе</strong> производства и <strong>обмена</strong>» [10, с. 426]. На самом деле Маркс и Энгельс здесь имеют в виду не <em>обмен</em>, а те <em>многочисленные отношения</em>, которые порождаются способами производства. Среди этих многообразных <em>отношений</em> располагается и <em>обмен</em>, как частная форма <em>отношений-вообще</em>.</p><p>Этот момент становится более понятным в контексте предмета исследования «Капитала» (1867). В немецком тексте «Das Kapital» стоит: «Was ich in diesem Werk zu erforschen habe, ist die kapitalistische Produktionsweise und die ihr entsprechenden Produktions- und <strong>Verkehrsverhältnisse</strong>» [17, s. 12]. Русский читатель читает: «предметом моего исследования является капиталистический способ производства и соответствующие ему <strong>отношения</strong> производства и <strong>обмена</strong>» [5, с. 6]. На самом же деле у Маркса имеет место более широкий контекст понимания <em>Verkehr</em>. Как нам представляется, немецкий мыслитель имеет в виду следующее: «предметом моего исследования является капиталистический способ производства и соответствующие ему Produktions- und <strong>Verkehrsverhältniße</strong>» [17, s. 12], т. е. «<strong>отношения</strong> производства и <strong>общения</strong>». Стало быть, Маркс имеет в виду то, что он исследует капиталистический способ производства и порождаемые этим способом производства производственные отношения («отношения производства») и <em><strong>«отношения общения»</strong></em>, т. е. <em>общественные отношения в самых разных сегментах человеческого бытия</em> («социального универсума»). Так, в подстрочном примечании 1 Маркс говорит «о связи между производственными отношениями и способом производства» [5, с. 6] («über den Zusammenhang zwischen Produktionsverhältnissen und Produktionsweise» [17, s. 11]).</p><p>Фраза «отношения производства и отношения обмена» выглядит просто нелепо в свете примечания к «Немецкой идеологии» (1845–1846), где редакторы указали, что, дескать, в понятии <em>Verkehr</em> нашло своё выражение формировавшееся тогда у основоположников понятие <em>производственных отношений</em> [11, с. 590–591]. В. Ф. Шелике справедливо возмущается: «Понять на этой основе, что же имел в виду Маркс (подставьте, получится: «Предметом моего исследования является капиталистический способ производства и соответствующие ему отношения производства и производственные отношения»),— невозможно в принципе» [14]. Более того, во Введении к Grundriße (1957) Маркс прямо указывает, что он желает исследовать не просто «обмен», а именно «Produktionsverhältnisse und Verkehrsverhältnisse» [21, s. 43],— благо, что здесь дан верный перевод: «Производственные отношения и отношения общения» [8, с. 46].</p><p>Не подлежит сомнению, что для Маркса <em>Verkehr</em> — это фундаментальное понятие, посредством которого описывается <em>движение любых социальных структур</em>, в том числе и экзистенциальных: то, как люди производят и в силу самой организации производства общаются; то, как в зависимости от производственных структур они обмениваются (дарообменом, грабежом, бартером, куплей-продажей); то, как эти отношения производства, обмена и распределения постепенно переносятся на межличностное общение (например, меркантильные отношения-к-Другим в буржуазном обществе [8, с. 103– 108]), коммуникацию, на отношения между 240 родителями и детьми, между мужчиной и женщиной [9, с. 115–116], внутриклассовые и межклассовые отношения; наконец, то, как внутренний, интимный мир постепенно приходит в соответствие господствующему способу производства и формам общения, им детерминированных (например, если всё продаётся, то и любовь,— а <em>Verkehr</em> — это и половая связь [«без общения и дети на свет появиться не могут, а следовательно без общения не будет людей и прекратится всякая история, как прекратится она и без производства средств для жизни!» [14]],— тоже начинает проституироваться). Соответственно, базисные (производственные) формы <em>Verkehr’а</em> постепенно, шаг за шагом пронизывают, пропитывают собой человеческие отношения вообще (от отношения к вещам до экзистенциального отношения к самому себе).</p><p>Таким образом, <em>Verkehr</em> следует понимать в контексте марксового понятия <em>человеческого отношения к миру</em> [12], в рамках развёртывания которого надо учитывать имманентную диалектику человеческого способа бытия в мире — диалектику праксиса: в процессе реализации праксиса человек вступает в <em>отношения</em> с предметностью, данной ему в его непосредственном окружении, преобразует эту предметность, и тем самым конституирует свою <em>окружающую предметную среду</em> (<em>Umwelt</em>). Но так как праксис всегда осуществляется совместно с Другими, то отношения-общения с ними формируют <em>мир людей</em> (<em>Mitwelt</em>), который, будучи интериоризирован в психике индивида, образует его <em>собственный мир</em> (<em>Eigenwelt</em>). Стало быть, <em>мир</em> можно понять только через <em>человека</em>, созидающего и осваивающего этот мир, <em>человеческим</em> образом <em>относящегося к миру</em>, а <em>человека</em> можно понять только через тот <em>мир</em>, в котором этот человек обретается [3, с. 211–220].</p><h3 id="außerung-и-aneignung" tabindex="-1"><a class="header-anchor" href="#außerung-и-aneignung"><span>Äußerung и Aneignung</span></a></h3><p>Основой того, как человек обретается в этом мире, с точки зрения Маркса, является преобразование этого мира в процессе <em>диалектического взаимодействия</em> с ним, в результате чего <em>созидается новое</em> [4]. Такое взаимодействие осуществляется в двух процессах: <em>Äußerung</em> и <em>Aneignung</em>. Для Маркса <em>сущностью человека</em> является сознательная <em>преобразующая</em> продуктивная деятельность. Что же происходит в процессе развертывания этой деятельности? В самом общем виде следующее: у человека, как живого существа, имеются потребности, которые сначала идеально репрезентируются в виде некоторых <em>целей</em> (образов, предвосхищающих будущий результат деятельности) и порождают в сознании <em>образ</em>, <em>идею предмета</em>, необходимого для удовлетворения <em>этой</em> потребности. Затем <em>цели</em> и <em>идеи</em>, <em>порожденные потребностью</em>, посредством трудовых операций <em>оформляются</em> и <em>воплощаются</em> в природном материале и тем самым человек <em>производит-для-себя предметность</em>, <em>новое, искусственное бытие</em>, не встречающееся в естественной эволюции природы <em>вне</em> преобразующей субъект-объектной активности человека.</p><p>Иначе говоря, <em>внутреннее</em> человека (проекты, идеи, эмоции, знания, в целом — его <em>душа</em>) <em>переходит</em> во <em>внешнюю</em> объективную вещь (Маркс этот момент праксиса называет термином <em>Äußerung</em> — <em>отсвоением</em>, <em>проявлением себя в мире</em>) и застывает в ней, <em>опредмечивается</em>. Обратный переход воплощенного в предмете идеального снова к человеку осуществляется только в процессе <em>распредмечивания</em> (<em>Aneignung</em>, <em>Genuß</em>, <em>усвоения</em>, <em>делания-предмета-своим</em>, <em>присвоения предмета себе</em>), когда «покоящееся свойство» (ruhende Eigenschaft) из предмета обратно переходит в форму деятельности (Unruhe) [5, с. 191–192], т. е. когда другие люди, используя предметность (пользуясь вещью, читая текст, слушая мою речь или музыку, воспринимая и интерпретируя мои жесты, выражение моего лица, воспринимая моё настроение), в своих действиях воспроизводят заложенное мною в ней <em>идеальное</em> содержание (<em>орудийную логику предмета</em>).</p><p>Но этот живой диалектический процесс перетекания, высвечивания друг в друге <em>Äußerung</em> и <em>Aneignung</em> в русских переводах бездушно передают с помощью диалектики категорий <em>производства</em> и <em>потребления</em>. Да, <em>формально</em> это верно, ибо именно через деятельность <em>производящую</em> и деятельность <em>потребляющую</em>, через <em>производство</em> материальных и духовных предметов и их <em>потребление</em> в процессах опредмечивания и распредмечивания люди <em>связываются</em> друг с другом в социальную тотальность: в действительности человек «существует, с одной стороны, как созерцание общественного бытия и действительное пользование [wirklicher Genuß] им, а с другой стороны — как тотальность человеческого проявления жизни [Totalität menschlicher Lebensäußerung]» [9, с. 591].</p><p>Но посмотрите, как, в противоположность сухим <em>производству</em> и <em>потреблению</em>, Маркс говорит, например, о феномене <em>присвоения</em> (<em>Aneignung</em>): «Присвоение <em>человеческой</em> действительности [Die Aneignung der <em>menschlichen</em> Wirklichkeit], её отношение к предмету, это — <em>осуществление на деле человеческой действительности</em>, человеческая <em>действенность</em> и человеческое <em>страдание</em>, потому что страдание, понимаемое в человеческом смысле, есть самопотребление [Selbstgenuß] человека» [9, с. 592]. А ниже Маркс добавляет: «Поэтому человеческая действительность столь же многообразна, как многообразны <em>определения</em> человеческой <em>сущности</em> и человеческая <em>деятельность</em>» [9, с. 592].</p><h3 id="okonomie" tabindex="-1"><a class="header-anchor" href="#okonomie"><span>Ökonomie</span></a></h3><p>Много путаницы возникало и возникает в связи с употреблением Марксом понятия <em>экономика</em> и производных от него терминов. Большей частью интерпретаторы Маркса исходят из того, что автор «Капитала» был «экономистом» в узком, современном смысле этого слова, хотя марксова политическая экономия никакого отношения к economics не имеет, она больше соответствует современным междисциплинарным исследованиям, объединяющим социальную философию, макроэкономический анализ, историю, социологию, социальное прогнозирование, социологию знания и даже этику. Стоит напомнить, что основатель классической политэкономии Адам Смит был профессором моральной философии. В этом смысле Маркс не был «чистым экономистом», экономистом par excellence.</p><p>Более того, следует признать, что марксово употребление термина «экономика» в большей степени восходит не к Адаму Смиту, Джону Стюарту Миллю или Давиду Риккардо, а к… <em>Аристотелю</em>. Известное разделение Аристотелем экономической деятельности на <em>собственно экономику</em> («домашнее», ойкосное — индивидуальное и полисное — хозяйство, т. е. хозяйственную деятельность, направленную на удовлетворение потребностей, что очень хорошо передаётся немецким словом <em>Wirtschaft</em>) и <em>хрематистику</em> (хозяйственную деятельность, осуществляемую с целью накопления богатства; у Маркса можно встретить термин <em>Judentum</em>, <em>еврейство</em>, как синоним ростовщичества) [1] было воспринято Марксом с воодушевлением, и выразилось в различении двух значений понятия «экономический»: как собственно <em>хозяйственной деятельности</em>, и как <em>господства товарного производства и накопления капитала</em>.</p><p>Такое различение двух семантик экономики у Маркса особенно важно для адекватного понимания рубрики <em>«общественная формация»</em>, ибо все формации являются экономическими в первом (ойкосном) смысле, но только капиталистическая формация есть формация экономическая во втором (хрематистическом) смысле. Именно из-за этого непонимания и возникали разного рода трудности в общей интерпретации материалистического понимания истории и теории общественных формаций.</p><p>Следует отметить, что К. Маркс в основном употребляет термин «<em>экономическая общественная</em> формация», а не «<em>общественно-экономическая</em> формация». Разница между понятиями, обозначенными этими терминами, фундаментальна. <em>Все</em> общественные формации являются «общественно-<em>экономическими</em>», ибо понятие <em>«экономика»</em> в данном случае употребляется в <em>широком смысле</em> как <em>производство материальных благ</em>, как <em>хозяйство</em> (что мы встречаем уже у Ксенофонта, согласно которому экономика — <em>τὰ οἰκονομικά, ἡ οἰκονομία</em> — есть <em>заведование домашним хозяйством</em>, <em>домохозяйство</em>, а <em>ó οἰκονομικὴ τέχνη</em> — <em>искусство ведения хозяйства</em>; об Аристотеле мы сказали выше), но только одна формация оказывается собственно <em>экономической общественной</em>,— здесь понятие «экономика» используется уже в <em>узком смысле</em> как <em>производство продуктов в качестве товаров</em>, и хозяйство рассматривается уже не как производство материальных благ для удовлетворения материальных потребностей, то есть не как производство потребительных ценностей, а как производство стоимостей <em>меновых</em>, ради них же самих (т. е. как хрематистика в аристотелевском смысле).</p><p>Именно поэтому политическая <em>экономия</em> как наука возникает именно в эпоху капитализма, в котором <em>экономический</em> (<em>товарно-денежный</em>) момент производства доведен до предела: производство ради прибыли, а не ради общественных потребностей людей, производство ради денег, а не ради человека. «Вот почему,— пишет Ф. Энгельс,— политическая экономия, наука о способах наживать деньги, является излюбленной наукой этих торгашей. Каждый из них — политико-эконом» [16, с. 497].</p><p>Изначально политическая экономия представляет собой «науку» о том, как «с тем же количеством труда произвести больше товара, следовательно, удешевить товары и ускорить накопление капитала» [5, с. 377]. Более того, буржуазная политическая экономия рассматривает только количественную сторону дела и меновую стоимость продуктов труда (в то время как классическая древность обращает внимание исключительно на качество и на потребительную ценность) что детерминируется тотальным характером товарно-денежных (меновых) количественных отношений. Именно в контексте такого словоупотребления необходимо, как представляется, понимать различение Марксом узкого и широкого смыслов понятий «экономика» и «экономический».</p><p>Таким образом, <em>только во вторичной формации экономические</em> (<em>товарно-денежные</em>) <em>отношения играют решающую роль</em>. Экономика в данном контексте понимается как <em>производство потребительных ценностей как продуктов частных работ, субъекты которых вступают в контакт исключительно путем обмена, то есть производство выступает в виде созидания меновых стоимостей</em>. Товарное производство достигает своего наиболее полного осуществления только в капиталистическом способе производства. В первичной же и третичной формациях в качестве производственного базиса выступают совершенно иные формы человеческой деятельности. Там нет «экономических отношений» в их <em>товарно-денежной</em> форме. Но это не означает, что там нет «экономики» (в смысле хозяйства). Поэтому <em>экономическая интерпретация истории как некоторая всеобщая парадигма, приложимая ко всей человеческой истории, у Маркса отсутствует</em>: экономическими факторами объясняются только способы производства в рамках экономической общественной формации, и прежде всего — в капиталистическом способе производства.</p><h3 id="dasensemble" tabindex="-1"><a class="header-anchor" href="#dasensemble"><span>Dasensemble</span></a></h3><p>В «Тезисах о Фейербахе» Маркс говорит, что «Aber das menschliche Wesen… In seiner Wirklichkeit ist… das <strong>ensemble</strong> der gesellschaftlichen Verhältnisse» [19, s. 6]. Маркс здесь употребляет французское слово <em>ensemble</em>, <em>ансамбль</em> (и даже пишет его не на немецкий манер с заглавной, а с прописной буквы, хотя и с артиклем среднего рода). В русском переводе это <em>ensemble</em> передано как <em>совокупность</em>: «Но сущность человека… в своей действительности… есть <strong>совокупность</strong> общественных отношений» [7, с. 3].</p><p>Различие сразу же бросается в глаза: если у Маркса речь идёт о <em>живой гармоничности</em> и <em>созвучности</em> (пусть даже и в смысле гераклитовской <em>ἁρ-μονία</em> как слаженности противоречий), то в русском варианте с «совокупностью» мы большей частью слышим коннотации <em>мёртвости</em>, <em>конгломерата</em>, <em>равнодушного соединения</em>. Отсюда и восприятие русским читателем марксова определения сущности человека как чего-то механического, как некоторой внутренне безразличной суммы общественных отношений, в то время как у него на самом деле речь идёт именно о <em>живой связи</em>, <em>живом общении</em> (<em>Verkehr</em>), о некоторой <em>сыгранности</em>: общественные отношения здесь, в 6-ом тезисе, выступают как бы в качестве музыкантов слаженно сыгранного коллектива.</p><h3 id="versachlichung" tabindex="-1"><a class="header-anchor" href="#versachlichung"><span>Versachlichung</span></a></h3><p>Одной из важнейших категорий, используемых Марксом в <em>Grundriße</em> и «Капитале», является понятие <em>Versachlichung</em>. В традиционном переводе этот термин Маркса переводят как <em>овеществление</em>. Но такой перевод радикально «уводит» нас от адекватного понимания того, что имеет в виду здесь Маркс. Русское <em>овеществление</em> означает <em>превращение в вещь</em>, и в этом отношении совпадает с <em>опредмечиванием</em>, т. е. с <em>воплощением</em>, <em>объективизацией</em> (сознания, труда) в некий предмет, в <em>вещное тело</em>. Для обозначения этого процесса Маркс обычно употребляет термин <em>Vergegenständlichung</em>, <em>опредмечивание</em>. Например, в рукописях 1844 года он пишет: «Продукт труда есть труд, закреплённый в некотором предмете, овеществлённый в нём, это есть <em>опредмечивание</em> труда. Осуществление труда есть его опредмечивание» [9, с. 560–561; 18, s. 511–512].</p><p>В некоторых ранних произведениях Маркс, ещё следуя гегелевской традиции, рассматривает опредмечивание как отчуждение, однако в более зрелых работах (и даже уже в тех же рукописях 1844 года) он чётко понимает, что опредмечивание представляет собой необходимый момент <em>любого</em> труда, в то время как феномен отчуждения возникает только при определённых социальных условиях, в которых осуществляется труд, а именно в условиях частной собственности, товарного производства и господства тотальных товарно-денежных отношений. Именно в таких условиях происходит <em>превращение социальных отношений из личных в вещные</em>, т. е. в <em>отношения вещей</em>: человеческая деятельность и отношения между людьми видятся только как отношения ролевые, т. е. <em>отношения между людьми принимают видимость отношений между вещами</em>. Вот <em>такой</em> процесс Маркс и называет термином <em>Versachlichung</em>, который следует передавать русским словом <em>овещнение</em> (как это в своё время предложил Г. С. Батищев [2, с. 459–477]), чтобы не путать его с овеществлением, опредмечиванием.</p><p>Таким образом, следует чётко различать у Маркса <em>«овеществление»</em> (<em>Ver<strong>gegenständ</strong>lichung</em>, вернее: <em>о<strong>предм</strong>ечивание</em>) и «о<em><strong>вещ</strong></em>нение» (<em>Ver<strong>sach</strong>lichung</em>). Именно в этом смысле Маркс говорит о «персонификации вещей и овеществлении [овещнении] лиц [Personifizierung der Sache und Versachlichung der Personen]» [5, с. 124] в товарно-денежном взаимодействии.</p><h3 id="menschlich" tabindex="-1"><a class="header-anchor" href="#menschlich"><span>Menschlich</span></a></h3><p>Это немецкое прилагательное практически везде переведено как <em>человеческий</em> (<em>die menschliche Arbeit</em> — <em>человеческий труд</em>, <em>die menschliche Gesellschaft</em> — <em>человеческое общество</em>). Но в немецком языке данное слово, помимо своего основного значения <em>человеческий</em>, означает также <em>человечный</em>, <em>гуманный; по-человечески сносный</em>; <em>достойный человека</em>. Поэтому, когда Маркс говорит о коммунизме как о <em>die menschliche Gesellschaft</em>, то он говорит не просто о <em>человеческом</em> обществе, а об обществе <em>человечном</em>. Любое общество — общество <em>человеческое</em>, но только общество коммунистическое, общество, в котором преодолено обесчеловечивающее отчуждение, является ещё и обществом <em>человечным</em>.</p><p>Только сквозь призму такого перевода можно понять слова Маркса о том, что «характер человека создается обстоятельствами (<em>Umständen</em>, <em>условиями жизни</em>), то надо, стало быть, сделать обстоятельства <strong>человечными</strong>» [11, с. 145–146] и «обстоятельства в такой же мере творят людей, в какой люди творят обстоятельства» [11, с. 37], то и религию можно элиминировать не путём расстрелов священников и разрушения храмов в духе «воинствующего материализма» В. И. Ленина и Е. Ярославского, а только путём практического устранения земной основы религии, то есть того «бессердечного мира», в котором обретается человек.</p><h3 id="выводы" tabindex="-1"><a class="header-anchor" href="#выводы"><span>Выводы</span></a></h3><p>Проблема адекватного прочтения и перевода основных терминов Маркса является уже давным-давно назревшей. И её решение должно привести к радикальной ревизии той философской терминологии Маркса, которая сложилась на сегодняшний день как в отечественном, так и в зарубежном марксоведении. Сегодня уже нельзя читать Маркса сквозь сталинские категориальные схемы и матрицы, к самому Марксу никакого отношения не имеющие. А ведь этими устоявшимися схемами и стереотипами искренне пользуются не только критики марксизма, но и сами марксисты, не отдавая себе отчета, что это — <em>не марксизм</em>. Мы видим, что в некоторых случаях те или иные понятия Маркса, даже погруженные в экономический контекст, нельзя переводить словами с явно производственно-техническими коннотациями, ибо тем самым ускользает философско-антропологический и даже в некотором отношении экзистенциальный смысл марксовой терминологии. В силу всего этого, как нам представляется, сегодня назрела потребность не только в новых переводах основных работ немецкого мыслителя, но и необходимость в радикальном переосмыслении всей его философии.</p><p>Наконец, наше небольшое разыскание хотелось бы завершить вопросом Ф. Энгельса, относящимся как раз к переводам текстов К. Маркса: «Мы вправе спросить: «Понимаешь ли ты то, что ты читаешь?» [15, с. 241].</p><h3 id="библиография-1" tabindex="-1"><a class="header-anchor" href="#библиография-1"><span>Библиография</span></a></h3><ol><li><p>Аристотель. Политика // Аристотель. Сочинения: В 4-х т. Т. 4 / Пер. с древнегреч.; общ. ред. А. И. Доватура.— М.: Мысль, 1983. С. 375–644.</p></li><li><p>Батищев Г. С. Проблемы и трудности перевода некоторых Марксовых философских понятий // Хамидов А. А. Философия истории Карла Маркса.— Алматы.: Институт философии, политологии и религиоведения КН МОН РК, 2014. С. 459–477.</p></li><li><p>Кондрашов П. Н. Онтологические структуры историчности: Исследование философии истории Карла Маркса / Под ред. К. Н. Любутина.— М.: Книжный дом «ЛИБРОКОМ», 2014.— 320 с. (Размышляя о марксизме. № 96).</p></li><li><p>Кондрашов П. Н. Философия праксиса Карла Маркса // Вопросы философии. 2016. № 10.</p></li><li><p>Маркс К. Капитал I // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1960. Т. 23. С. 5–784.</p></li><li><p>Маркс К. Письмо П. В. Анненкову, 28 декабря 1846 г. // К. Маркс, Ф. Энгельс. Сочинения.— М.: Политиздат, 1962. Т. 27. С. 401–412.</p></li><li><p>Маркс К. Тезисы о Фейербахе // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1955. Т. 3. С. 1–4.</p></li><li><p>Маркс К. Экономические рукописи 1857–1859 гг. // Маркс К., Энгельс Ф. Соч. 2-е изд.— М.: Политиздат, 1968. Т. 46. Ч. I. С. 3–508.</p></li><li><p>Маркс К. Экономическо-философские рукописи 1844 г. // Маркс К., Энгельс Ф. Из ранних произведений.— М.: Политиздат, 1956. С. 517–642.</p></li><li><p>Маркс К., Энгельс Ф. Манифест коммунистической партии // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1955. Т. 4. С. 419–459.</p></li><li><p>Маркс К., Энгельс Ф. Немецкая идеология // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1955. Т. 3. С. 9–544.</p></li><li><p>Шелике В. Ф. К. Маркс и Ф. Энгельс об общении как одном из видов человеческого отношения к миру // Диалектика познания, понимания, общения. Сборник научных трудов.— Фрунзе: КГУ, 1986. С. 84–93.</p></li><li><p>Шелике В. Ф. Непознанный Маркс и некоторые проблемы современности (Часть 3) // Философские науки. 2013. № 5. С. 120–130.</p></li><li><p>Шелике В. Ф. Обесчеловечивание Маркса «марксистско-ленинскими» переводами с немецкого // http://hanguest.narod.ru/Freunde/menschlich.htm.</p></li><li><p>Энгельс Ф. Как не следует переводить Маркса // Маркс К., Энгельс Ф. Соч., 2-е изд.— М.: Политиздат, 1961. Т. 21. С. 237–245.</p></li><li><p>Энгельс Ф. Положение рабочего класса в Англии // Маркс К., Энгельс Ф. Соч., 2-е изд. М.: Политиздат, 1955. Т. 2. С. 231–517.</p></li><li><p>Marx K. Das Kapital // Marx K., Engels F. Werke. B. 23.— Berlin: Dietz Verlag, 1962. S. 11–802.</p></li><li><p>Marx K. Ökonomisch-philosophischen Manuskripten aus dem Jahre 1844 // Marx K., Engels F. Werke. Bd. 40.— Berlin: Dietz Verlag, 1968. S. 465–588.</p></li><li><p>Marx K. Thesen über Feuerbach // Marx K., Engels F. Werke. B. 3.— Berlin: Dietz Verlag, 1978. S. 5–7.</p></li><li><p>Marx K., Engels F. Die heilige Familie // Marx K., Engels F. Werke. B. 2.— Berlin: Dietz Verlag, 1962. S. 3–223.</p></li><li><p>Marx K., Engels F. Einleitung zu den Grundrissen der Kritik der politischen Ökonomie // Marx K., Engels F. Werke. Bd. 42.— Berlin: Dietz Verlag, 1983. S. 15–768.</p></li><li><p>Marx K., Engels F. Manifest der Kommunistischen Partei // Marx K., Engels F. Werke. Bd. 4.— Berlin: Dietz Verlag, 1977. S. 459–493.</p></li></ol><p>Автор выражает глубокую благодарность за критические замечания и помощь в работе над данной статьей Вальтраут Шелике, Георгию Шелике (Москва), Валерию Чеховскому (Подстдам, Германия), Рольфу Хеккеру (Берлин, Германия) и Анастасии Артёмовой (Екатеринбург).</p>',85))])}const k=s(u,[["render",g]]),f=JSON.parse('{"path":"/%D0%BA%D0%BE%D0%BD%D0%B4%D1%80%D0%B0%D1%88%D0%BE%D0%B2-%D0%BA%D0%B0%D0%BA%D0%BE%D0%B3%D0%BE-%D0%B6%D0%B5-%D0%BC%D0%B0%D1%80%D0%BA%D1%81%D0%B0-%D0%BC%D1%8B-%D1%87%D0%B8%D1%82%D0%B0%D0%B5%D0%BC.html","title":"Какого же Маркса мы читаем?","lang":"ru-RU","frontmatter":{"format":"comtext","version":"2.0.0","title":"Какого же Маркса мы читаем?","author":["Петр Николаевич Кондрашов"]},"git":{},"filePathRelative":"кондрашов-какого-же-маркса-мы-читаем.md"}');export{k as comp,f as data};
