import{_ as p,c as r,b as s,a,d as f,w as n,r as i,o as l,e}from"./app-CfGhvXUl.js";const g={},c={class:"table-of-contents"};function u(d,o){const t=i("router-link");return l(),r("div",null,[o[5]||(o[5]=s("h1",{id:"труд-производительныи-и-труд-образующии-ценность",tabindex:"-1"},[s("a",{class:"header-anchor",href:"#труд-производительныи-и-труд-образующии-ценность"},[s("span",null,"Труд производительный и труд, образующий ценность")])],-1)),s("nav",c,[s("ul",null,[s("li",null,[f(t,{to:"#i-понятие-производства-у-маркса-и-буржуазных-экономистов-общее-определение-производства"},{default:n(()=>[...o[0]||(o[0]=[e("I. Понятие производства у Маркса и буржуазных экономистов. Общее определение производства.",-1)])]),_:1})]),s("li",null,[f(t,{to:"#ii-производство-нематериальных-потребительных-ценностеи-в-меновом-хозяистве"},{default:n(()=>[...o[1]||(o[1]=[e("II. Производство нематериальных потребительных ценностей в меновом хозяйстве.",-1)])]),_:1})]),s("li",null,[f(t,{to:"#iii-товарныи-фетишизм"},{default:n(()=>[...o[2]||(o[2]=[e("III. Товарный фетишизм.",-1)])]),_:1})]),s("li",null,[f(t,{to:"#iv-естественно-необходимыи-и-общественно-необходимыи-труд-так-называемые-издержки-обращения"},{default:n(()=>[...o[3]||(o[3]=[e("IV. Естественно-необходимый и общественно-необходимый труд. Так называемые «издержки обращения».",-1)])]),_:1})]),s("li",null,[f(t,{to:"#v-общественно-необходимыи-труд-и-общественныи-паразитизм"},{default:n(()=>[...o[4]||(o[4]=[e("V. Общественно-необходимый труд и общественный паразитизм.",-1)])]),_:1})])])]),o[6]||(o[6]=a('<p>Издание С. Дороватовского и А. Чарушникова.</p><p>Цена 40 коп.</p><p>С.-ПЕТЕРБУРГ.</p><p>Тип. Т-ва «НАРОДНАЯ ПОЛЬЗА».</p><p>Коломенская ул., собств. дом № 39.</p><ol start="1899"><li></li></ol><p>Дозволено цензурою. Спб. 5 Июля 1899 года.</p><h2 id="i-понятие-производства-у-маркса-и-буржуазных-экономистов-общее-определение-производства" tabindex="-1"><a class="header-anchor" href="#i-понятие-производства-у-маркса-и-буржуазных-экономистов-общее-определение-производства"><span>I. Понятие производства у Маркса и буржуазных экономистов. Общее определение производства.</span></a></h2><p>Политическую экономию определяют обыкновенно, как науку, изучающую общественные отношения производства в их историческом развитии.</p><p>Таким образом, экономическое исследование любой общественной формации сводится к разрешению двух вопросов. Во-первых, предстоит определить предмет изучения в каждом частном случае, другими словами, выяснить — какие формы человеческой деятельности должны быть отнесены к категории общественного производства данной исторической эпохи. Во-вторых, анализировать те общественные отношения, который возникают в этой определенной области.</p><p>Уже с первого взгляда очевидно, что между указанными двумя сторонами экономического исследования существует неразрывная связь.</p><p>Общественные отношения суть прежде всего отношения <strong>производственные</strong>, они предполагают, следовательно, определенные формы производительной деятельности, как свое необходимое содержание; с другой стороны, данная форма человеческого труда имеет место и является производительной не сама по себе, но лишь при условии существования определенной общественной организации. Пока мы рассматриваем процесс труда абстрактного, пока мы имеем в виду голый факт воздействия человека на внешнюю природу, мы совершенно не в состоянии квалифицировать те или другие конкретные виды труда, как производительные или непроизводительные. И если понятие «производительный труд» формулируется иногда совершенно абстрактно, независимо от всякой конкретной формы отношений между людьми, то в действительности определенная общественно-экономическая формация всегда предполагается при этом, как нечто данное. Различие между отдельными экономистами и отдельными школами экономистов по отношению к вопросу о «производстве» заключается в том, что в одних случаях связь между производительным характером тех или других видов труда и породившей их общественной организацией «сознается» автором и формулируется ими, в других случаях связь эта выступает лишь в области «бессознательного» и потому не получает формулировки, но только подразумевается.</p><p>К первой категории экономистов должен быть отнесен прежде всего К. Маркс. В I томе «Капитала» в главе «Процесс труда и процесс производства прибавочной ценности» мы читаем: «В процессе труда деятельность человека достигается при помощи рабочих орудий заранее предположенного изменения в предмете труда. Завершением этого процесса является продукты. Продукт есть потребительная ценность, вещество природы, приспособленное к удовлетворению человеческих потребностей... При рассмотрении всего процесса с точки зрения его результата, т. е. продукта, и рабочее орудие, и предмет работы являются средствами производства, а сама работа — производительными трудом»<sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup>.</p><p>Здесь процесс труда рассматривается совершенно независимо от исторически определенных его форм.</p><p>Однако, тотчас же вслед за этими абстрактным определением Маркс считает необходимыми сделать следующую оговорку: «Это определение производительного труда, полученное нами с точки зрения простого процесса труда (т. е. с точки зрения процесса труда, взятого абстрактно. <strong>В. Б.</strong>), оказывается совершенно недостаточным для капиталистического процесса производства»<sup class="footnote-ref"><a href="#fn2" id="fnref2">[2]</a></sup>. В самом деле, продукт капиталистического производства не только потребительная ценность, но и меновая, не только «полезность», но и товар. Да и это еще не все: «Капиталистическое производство не есть только производство товаров, но есть, по преимуществу, производство прибавочной ценности»<sup class="footnote-ref"><a href="#fn3" id="fnref3">[3]</a></sup>.</p><p>Понятное дело, что производство прибавочной ценности, образуя «основание капиталистической системы», должно быть принято во внимание при анализе условий, необходимых для того, чтобы труд в капиталистическом обществе мог быть производительным.</p><blockquote><p>«Рабочий производит не для себя, а для капитала. Поэтому уже не достаточно, чтобы он только производил, он должен производить прибавочную ценность. Только тот рабочий производителен, который производит капиталисту прибавочную ценность, т. е. служит для самовозрастания капитала»<sup class="footnote-ref"><a href="#fn4" id="fnref4">[4]</a></sup>.</p></blockquote><p>Впрочем, оговорка Маркса является еще недостаточной.</p><p>Маркс показал, что в капиталистическом обществе сама возможность приложения труда в том или другом направлении находится в зависимости от специфического характера капитализма, как особой общественной организации. Но в этом отношении капиталистическая система не представляет исключения в ряду остальных исторически известных нам общественных формаций. Имеем ли мы перед собою строго натуральное производство патриархального рода, или меновое хозяйство мелких самостоятельных товаропроизводителей средних веков — везде и всегда конечная цель производства те «заранее предположенные изменения», которые труд человека производит в предметах природы, диктуются отдельному производителю общественными отношениями данной экономической организации. Поэтому мы не можем вполне согласиться со следующими словами Маркса: «Для изучения исчезнувших общественно-экономических формаций остатки орудий производства имеют то же значение, как остатки костей для познания организации исчезнувших пород животных. Не то, <strong>что</strong> делается, различает между собой экономические эпохи, но то, <strong>как</strong> делается, при помощи <strong>каких</strong> орудий ведется производство в течение этих эпох»<sup class="footnote-ref"><a href="#fn5" id="fnref5">[5]</a></sup>. Это утверждение вряд ли может быть принято, по крайней мере в такой категорической форме. Скорее наоборот — не только способ производства, но и сама цель его, сама «потребительная ценность» имеет специфически общественный характер, носит на себе печать данной экономической эпохи. В дальнейшем изложении мы более подробно остановимся на этом вопросе. А пока нам важно отметить одно: хотя определение производительного труда у Маркса не достаточно обобщенно и вообще является незаконченным, но по отношению к капиталистическому производству Маркс вполне отчетливо формулировал то основное положение, что понятие производительного труда может быть установлено не in abstracto, но лишь в связи с определенной общественно-экономической организации. Совершенно противоположна основная точка зрения буржуазной политической экономии.</p><p>Все представители этого направления, начиная с физиократов и классиков и кончая вульгарными апологетами капитализма, исходят из того положения, что отношения менового хозяйства, и именно в его наиболее развитой капиталистической форме, являются единственными вообще возможными отношениями людей. Поэтому для них, капитализм является не исторически-преходящей общественно-экономической системой, но вечным условием существования человеческого рода, так что устраняется сам вопрос о связи между производительными трудом и общественной организацией производства. В противоположность Марксу и его школе, вся буржуазная экономия принимает вторую из указанных выше двух постановок вопроса о производстве и производительном труде.</p><p>Здесь капитализм постоянно предполагается, как нечто данное, как некоторое conditio, sine qua non какого бы то ни было производства, поэтому соотношение между производительным трудом и общественной организацией не может быть сформулировано; оно действует лишь в сфере бессознательного и находит свое внешнее выражение в том, что под <strong>фирмой</strong> производительного труда вообще выступает труд производительный в капиталистическом обществе. При этом задача исследователя чрезвычайно упрощается. Бюргерский экономист видит перед собой лишь изменяющуюся технику неизменной формы производства.</p><p>С этой точки зрения предмет политической экономии кажется определенным раз навсегда. Изучая изменяющиеся способы удовлетворения <strong>неизменных</strong> человеческих потребностей, политическая экономия имеет <strong>естественные</strong> границы, установленные самой природою, границы настолько точные и ясные, что, по-видимому, здесь совершенно не остается места каким бы то ни было недоразумениям. Поэтому вопрос о производительном и непроизводительном труде часто останавливает на себе гораздо меньше внимания, чем это бы следовало. Многие экономисты, указав в двух-трех словах, что разумеется ими под именем производительного труда, ограничиваются таким, беглоброшенным замечанием и затем уже не возвращаются к вопросу о производстве, как к вещи общеизвестной. Однако, на практике каждому экономисту сплошь да рядом приходится наталкиваться на такие формы человеческой деятельности, о которых очень трудно сразу сказать, относятся ли они к сфере производства, или нет. Отсутствие прочно установленного критерия приводит к тому, что даже у авторов одной и той же научной школы обнаруживается значительное разнообразие воззрений при отнесении некоторых видов человеческого труда к категории производительного или непроизводительного.</p><p>Настоящая статья представляет попытку рассмотреть в общей связи некоторые наиболее существенные вопросы, возникающее при определении понятия «производство». При этом необходимо уже заранее оговориться, что изложение отнюдь не претендует на полноту и почти не затрагивает исторического развития научных идей, обнимающих данный вопрос. Мы будем иметь в виду преимущественно <strong>одно</strong> научное направление и именно то, которое ведет свое начало от «Zur Kritik der politischen Oekonomie», и вся наша задача исчерпывается указанием некоторых неточностей и противоречий, мешающих, по нашему мнению, системе Маркса-Энгельса принять вполне стройный и законченный вид.</p><p>В процессе борьбы за существование различные представители животного и растительного мира вырабатывают чрезвычайно разнообразные приспособления, до бесконечности видоизменяющие сам характер борьбы. Особенности организации каждого биологического вида составляют, как известно, результат таких приспособлений и, следовательно, в конечном итоге определяются той специальной ролью, которая выпала на долю данного вида в общей жизненной борьбе.</p><p>Характеристическая особенность вида Homo sapiens заключается, как известно, в том, что совершенствующее действие подбора направляется здесь преимущественно на психику, на интеллект. Соответственно этому, мерилом большей или меньшей приспособленности человека к его среде является не совершенство чисто физических функций и соответственных органов его тела, а совершенство приемов <strong>сознательной</strong> борьбы с природою и тех внешних средств — например, орудий — которые он ставит между собою и внешнею природою в процессе сознательной борьбы с нею, в процессе <strong>труда</strong>. Другая, тесно связанная с первой особенность человеческой борьбы за существование — это ее <strong>общественный</strong> характер. Таким образом, специфически-человеческой формой борьбы за жизнь является борьба общественно-трудовая, <strong>иначе — производство</strong> или <strong>производительный труд</strong>.</p><p>Подобно всякому другому виду борьбы за существование, производство представляет из себя совокупность процессов, необходимых в данном обществе для сохранения его жизни, для непрерывного воспроизведения общественного целого, как особой жизненной формы. Процесс общественной жизни сводится к удовлетворению ряда потребностей, строго определенных для каждой исторически-данной общественной формации; следовательно, «производительной деятельностью» или «производительным трудом» мы называем всякую целесообразно-направленную затрату человеческой энергии, имеющую своим объективным результатом удовлетворение известной общественной потребности. Другими словами, труд должен быть признан производительным, когда продукты его являются «потребительной ценностью» и притом не индивидуальной только, но <strong>общественной</strong> потребительной ценностью.</p><p>Последний пункт может возбудить некоторые недоразумения. Возникает вопрос, существуют ли вообще <strong>общественные</strong> потребительные ценности. Ведь потребление есть акт строго индивидуальный. Даже на спартанских сисситиях сам процесс потребления отнюдь не является общественным. Совместное потребление не может принять ни формы сотрудничества, ни, тем более, формы разделения труда, — оно лишено этих основных признаков кооперации, без которых деятельность отдельных людей никогда не может стать общественным актом, как бы тесно ни соприкасались эти люди в пространстве и времени. Итак, поскольку мы рассматриваем сам процесс потребления, мы совершенно лишены возможности провести демаркационную линию между потребительной ценностью индивидуальной и общественной.</p><p>Но нетрудно убедиться, что «потребление» только по недоразумению<sup class="footnote-ref"><a href="#fn6" id="fnref6">[6]</a></sup> может быть включено в область политической экономии, и потому тот или другой его характер никоим образом не имеет определяющего значения для экономических понятий. Потребление, процесс реализации потребительной ценности есть в то же время процесс ее разрушения; потребление представляет первую фазу того круговорота веществ, который, взятый в целом, называется жизнью организма, поэтому единственная наука, имеющая дело с потреблением — есть физиология. Предмет политической экономии не акт потребления, уничтожающий продукты производства, но самое это производство, взятое с его общественной стороны, т. е. те отношения людей, который создаются производством и его непосредственным результатом — распределением. В сфере производства только еще образуются предметы потребления, в сфере распределения предметы потребления уже созданы, но во всяком случае они фигурируют здесь не как реализованные, а как <strong>потенциальные</strong> предметы потребления. Поэтому потребительной ценностью называется не действительно потребляемый предмет, но предмет — <strong>пригодный</strong> к непосредственному потреблению, — пригодный как по своим физико-химическими свойствам, так и по общественным условиям своего существования.</p><p>Выражение «общественная потребительная ценность» вовсе не значит, что предметы потребляются сообща, — оно требует только, чтобы предмет был способен удовлетворить не <strong>исключительную</strong> потребность данного лица, но потребность организации данного общества.</p><p>Если отвлечься от логической фикции Робинзона, столь любезного экономистам всех времен и народов, то везде мы найдем <strong>общественное</strong> производство.</p><p>Даже в ту отдаленную эпоху, когда самодовлеющей экономической единицей был род, характер отдельных работ и общее их назначение определялось потребностью коллективного производства и, следовательно, носило на себе печать общественных отношений первобытно-родовой эпохи.</p><p>Такими образом, общественные потребительные ценности несомненно существовали во все исторические эпохи. Но тут мы наталкиваемся на затруднение другого рода. Не только в первобытно-родовом обществе, но и в современном — товарном возможны такие случаи, когда известный предмет уже заранее предназначается для удовлетворения определенной потребности отдельного лица. Считать в таких случаях труд человеческий потраченным непроизводительно — далеко не всегда представляется возможным. В самом деле, неужели мы должны признать непроизводительным труд крестьянина, когда он сам потребляет произведенный им хлеб, и производительным, когда этот хлеб фигурирует на рынке, как потенциальный предмет потребления для всех?</p><p>Что касается собственно крестьянина, то в данном случае вопрос осложняется еще промежуточным положением крестьянского хозяйства, играющего роль переходной формы от натурального производства к денежному. Крестьянское хозяйство является амфибией, совмещающей в себе основные признаки двух совершенно различных производственных организмов. Как часть товарно-менового общества, оно имеет критерием производительности труда те общие условия, при которых продукт становится <strong>товаром</strong>, т. е. потребительной ценностью для неопределенного и широкого круга потребителей, связанных между собою рынком; как пережиток натурально-хозяйственной эпохи, крестьянская семья является самодовлеющей экономической ячейкой и потому потребности семьи определяют собою размеры ее производства.</p><p>С точки зрения натурального хозяйства крестьянской семьи, хлеб во всяком случае представляет из себя <strong>общественную</strong> потребительную ценность, следовательно, должен быть признан продуктом <strong>производства</strong>, а труд, потраченный на изготовление — <strong>производительным</strong> трудом. Но этого мало. Возможен такой случай, когда известный продукт предназначается только для удовлетворения потребности отдельного члена общества, и все-таки труд его изготовления должен быть признан производительным. Положим, производитель натурального хозяйства приготовил какое-нибудь орудие труда, например, топор, исключительно для своего собственного употребления. Если топор необходимое орудие производственной деятельности при данной общественной организации, то, очевидно, он представляет <strong>общественную</strong> потребительную ценность даже в том случае, когда фактически предназначается только для одного определенного индивидуума. В самом деле, без топора наш производитель не в состоянии выполнить той функции, которая принадлежит ему в системе общественного производства. Если бы он сам не сделал топора, они должен был бы взять его у других, поглотив, таким образом, определенную часть средств производства, находящихся в распоряжении общества. Очевидно, труд, потраченный на приготовление топора, уменьшает затрату энергии со стороны других членов общества, труд этот направлен к сохранению и поддержанию жизни не только непосредственного владельца топора, но и всего общественного целого, а следовательно — он вполне подходит под то общее определение, которое мы выше дали производству.</p><p>Итак, предметом политической экономии является производство общественных потребительных ценностей; сюда относятся только те виды труда, которые имеют своим результатом повышение жизнедеятельности общества, взятого в целом. Всякие затраты человеческой энергии, не создающие предметов <strong>общественного</strong> потребления, не относятся к сфере производства, eo ipso не подлежат рассмотрению экономической науки. Такие <strong>непроизводительные</strong> затраты энергии, конечно, также существуют во все исторические эпохи.</p><p>Само собою разумеется, что деятельность, лишенная основных признаков труда, деятельность, не направленная к определенной, заранее намеченной цели, не может входить в сферу производства, так как она вообще не воплощается ни в каких потребительных ценностях, не создает предметов потребления ни индивидуального, ни общественного характера. Однако, и труд не всегда относится к производству.</p><p>Предположим, например, что отдельный член общества с какими бы то ни было целями — хотя бы из чувства мести — разрушает предметы потребления других членов общества. Деятельность такого человека носит характер труда, она целесообразно направлена, удовлетворяет известной его <strong>личной</strong> потребности и ее результаты представляют потребительную ценность для ее автора, но так как эта деятельность не только не создает потребительной ценности для общества, как целого, но даже явным образом уничтожает условия его существования, то она является в полном смысле слова <strong>непроизводительным</strong> трудом и потому совершенно исключается из области экономического исследования.</p><h2 id="ii-производство-нематериальных-потребительных-ценностеи-в-меновом-хозяистве" tabindex="-1"><a class="header-anchor" href="#ii-производство-нематериальных-потребительных-ценностеи-в-меновом-хозяистве"><span>II. Производство нематериальных потребительных ценностей в меновом хозяйстве.</span></a></h2><p>Наметив границы понятия «производство», переходим к его ближайшему анализу.</p><p>Прежде всего очевидно, что само содержание отдельных общественных потребностей не представляет для нас никакого интереса. Правда, как уже было упомянуто, общественная жизнь в каждую данную историческую эпоху складывается из удовлетворения вполне <strong>определенных</strong> потребностей, но политическая экономия не имеет ни малейшей надобности исследовать условия возникновения каждой потребности в отдельности. Для нее определяющим моментом является сам общественный характер данной потребительной ценности.</p><p>Чем бы ни была обусловлена известная потребность, но раз она стала <strong>общественной</strong>, то отсюда уже само собой следует, что она в то же время <strong>общественно-необходима</strong>, т. е. не представляется чем-либо случайным, но неизбежно вытекает из условий существования данного общества.</p><p>С точки зрения производства, все общественные потребности одинаково законны. «Природа этих потребностей, есть ли, например, их источник желудок или воображение, нисколько не изменяет сущности дела<sup class="footnote-ref"><a href="#fn7" id="fnref7">[7]</a></sup>», т. е. во всяком случае человеческая энергия, затраченная на их удовлетворение, будет производительным трудом.</p><p>В такой абстрактной форме по отношению к производству вообще вышеприведенное положение Маркса вряд-ли может встретить серьезное возражение с чьей либо стороны. Как бы ни определялось понятие производства, необходимо во всяком случае признать, что <strong>естественные</strong> различия потребностей нисколько не препятствуют одинаковым <strong>общественным</strong> способам их удовлетворения, и политическая экономия, как одна из наук об обществе, должна, очевидно, совершенно оставить в стороне эти естественные различия потребностей.</p><p>Однако, вопрос этот, совершенно ясный и простой, пока мы оперируем над абстрактными понятием производства вообще, значительно усложняется, когда предстоит анализировать какую-нибудь определенную форму производства, особенно современную — товарно-капиталистическую его форму.</p><p>Суть общественного отношения, придающего продуктам человеческого труда форму товаров, в наиболее общем виде выражается следующими равенством:</p><p>X товара А = Y товара В. Это — так называемая простая или неразвитая форма ценности. Посмотрим, при каких условиях эта «форма ценности», существующая только в головах людей, способна превратиться в реальное отношение фактически обмениваемых товаров.</p><p>Другими словами, какие общественные предпосылки необходимы для того, чтобы теоретическое представление X товара А = Y товара В<sup class="footnote-ref"><a href="#fn8" id="fnref8">[8]</a></sup> могло «из простой абстракции стать общественным результатом самого менового процесса <sup class="footnote-ref"><a href="#fn9" id="fnref9">[9]</a></sup>» в акте Т—Д—Т_1.</p><p>Таких общественных предпосылок две.</p><ol><li>Товар А должен быть потребительною ценностью для владельца В, но не должен представлять никакой потребительной ценности для собственного владельца.</li></ol><p>Это условие осуществляется общественным разделением труда.</p><ol start="2"><li>Товар А должен представлять потребительную ценность не только для владельца В, как индивидуума, но и для целой общественной группы, должен удовлетворять какой-либо <strong>общественной</strong> потребности, ибо только в последнем случае акт, меняющий местами X товара А и Y товара В, перестает быть случайной индивидуальной сделкой и приобретает общественный характер.</li></ol><p>Раз эти два условия даны, продукт человеческого труда имеет полную возможность стать товаром, меновою ценностью. Величина этой последней определяется, как известно, количеством труда, общественно-необходимого для изготовления данного продукта. При этом, поскольку мы анализируем процесс труда лишь со стороны происхождения меновой ценности, мы должны отвлечься от всяких качественных различий как в тех общественных потребностях, для которых предназначаются продукты труда, так и в тех конкретных видах труда, которые определяются характером производимых продуктов. Труд, как субстанция, образующая ценность, есть абстрактный и всеобщий труд, в котором уничтожаются все качественные различия, для проявления которого необходимо только, чтобы он удовлетворял <strong>какой-либо</strong> общественной потребности, но совершенно безразлично — <strong>какой именно</strong> потребности он удовлетворяет в каждом данном случае.</p><p>Абстракция всеобщего человеческого труда, сведение его к простой трате мускульной и нервной энергии, существует не только в головах ученых. В процессе рыночной конкуренции сведение это реализуется фактически: регулятором меновых отношений является именно простой человеческий труд, лишенный всякого определенного качества, причем затраты различных конкретных видов труда фигурируют просто, как количественные видоизменения этой однородной всеобщей субстанции. Таким образом, процесс обмена, круговорот товаров представляет в современном обществе ту сферу, где только и может проявиться «специфически-общественный характер независимых друг от друга частных работ»<sup class="footnote-ref"><a href="#fn10" id="fnref10">[10]</a></sup>.</p><p>Следовательно, продукт труда лишь в том случае может стать истинным товаром, если он способен выдержать круговорот товаров, способен продолжительное время фигурировать на рынке, не утрачивая своей потребительной ценности — этой необходимой качественной предпосылки его товарного бытия. По видимому, последняя возможность обусловлена в значительной степени чисто естественными, природными свойствами продукта. И прежде всего представляется несомненным, что лишь такой продукт способен к круговороту товаров, который является осязаемой, материальной вещью. В самом деле, потребительная ценность только при том условии может сохраняться неизменной в процессе менового обращения, если она связана с самими вещественными свойствами продукта, если она, следовательно, может быть передаваема из рук в руки вместе с передачей самого продукта. Для этого, во-первых, сам продукт должен быть материальным предметом, способным к такой передаче; во-вторых, потребительная ценность должна быть связана с вещественными свойствами продукта не формальным только образом, а всецело обусловливаться ими и только ими. Предметы потребления, совершенно нематериальные, как, например, публичная лекция или концерт, очевидно, не могут принять участие в круговороте товаров. Специфический характер их заключается в том, что они потребляются только в сам момент производства. Эта особенность нематериальных потребительных ценностей не препятствует признать их продуктами <strong>производительного</strong> труда, раз они удовлетворяют известной необходимой общественной потребности, но, по видимому, делает их неспособными принять вполне развитую товарную форму.</p><p>Итак, мы наталкиваемся на некоторые естественные препятствия, мешающие определенной категории продуктов производства превратиться в товары. Как кажется, к двум сформулированным выше <strong>общественным</strong> историческим предпосылкам товарного производства необходимо прибавить еще одну — <strong>естественную</strong>, абсолютную: только материальные предметы становятся товарами; производство невещественных предметов потребления навсегда обречено остановиться на той промежуточной стадии, которая, по терминологии Бюхера, носит название «Lohnwerk».</p><p>По всей вероятности, именно вышеприведенные соображения заставляют некоторых последователей Маркса, вопреки установленному последним основному принципу, отделять toto coelo потребности «желудка» и потребности «воображения».</p><p>Приступая к анализу производства не материальных предметов потребления, остановимся сначала на том промежуточном случае, когда потребительная ценность носить вполне идеологический характер, но в то же время неразрывно связана с известной материальной вещью, как необходимым условием ее проявления.</p><p>Перед нами небольшой томик стихотворений, на обложке которого, рядом с эпиграфом из Шекспира, красуется многозначительная надпись: «75 коп. серебром».</p><p>Представляют ли товар эти плоды «ума холодных наблюдений и сердца горестных замет»?</p><p>Уже с первого взгляда очевидно, что книга потребляется не в той вещественной форме, в которой она фигурирует на полке книгопродавца. Книга, как материальная вещь, как испещренная типографскими значками бумага, имеет самостоятельную потребительную ценность, совершенно отличную от потребительной ценности той же книги, как сборника стихотворений. Характер потребления печатной бумаги, как материальной вещи, вытекает из ее физико-химических свойств, но в качестве такой вещи бумага появляется на рынке лишь как отбросы литературного труда, отбросы, совершенно утратившие ту специфическую потребительную ценность, которая характеризует литературу, как особую отрасль общественного производства. Наоборот, поскольку книга является продуктом <strong>литературного</strong> производства, потребительная ценность ее определяется отнюдь не ее вещественными свойствами, но совокупностью вызываемых в читателе идей и образов, по отношению к которым материя книги — печатная бумага — служит лишь необходимым условием проявления. Таким образом, в рассматриваемом случае потребительная ценность предмета чисто внешним, формальным образом связана с его материальной сущностью. Спрашивается, препятствует ли это обстоятельство продукту поэтического творчества стать действительным, вполне развитым товаром? Очевидно — нет. Хотя связь между потребительной ценностью продуктов литературного производства и их вещественными материалом только формальная, тем не менее характер этой связи остается неизменными для всех имеющихся на рынке покупателей. В каждом из этих последних данное печатное произведение вызывает те же идеи и те же образы, и потому циркуляция книги вполне заменяет непосредственное товарное обращение самих идей и образов. Кроме того, не прямая связь между вещественными свойствами предмета и его потребительной ценностью отнюдь не составляет специфической особенности таких идеалистических сфер производства, как литература или музыка. Безделушки, украшающие дамский будуар, очевидно, представляют для их владельца потребительную ценность, совершенно отличную от этих же предметов, как кусков стекла, мрамора или гипса.</p><p>В предметах украшения точно так же, как и в литературных произведениях, вещественный материал лишь знак невещественных отношений, условие проявления потребительной ценности совершенно нематериального характера; но, как известно, это нисколько не препятствует предметам роскоши быть типическими товарами.</p><p>Таким образом, продукты литературного труда ничуть не менее способны принять участие в круговороте товаров, чем любой предмет, удовлетворяющий известной общественной потребности своими материальными свойствами. Книга, следовательно, такой же полноправный товар, как хлеб или вакса.</p><p>Фактическая организация современного литературного труда показывает, что естественные особенности нисколько не препятствуют ему принять форму вполне развитого товарного производства.</p><p>Общественные предпосылки, необходимые для того, чтобы продукт человеческого труда мог стать меновою ценностью, давно уже осуществлены в области литературы. Книга менее, чем какой-либо другой товар, может представлять потребительную ценность для ее производителя — будет ли это сам автор, или издатель; и в этом смысле пережитки натурального хозяйства исчезли в литературе гораздо полнее, чем, например, в производстве сапогов. Далее, в современном обществе книги рассчитывают не на потребности отдельных индивидуумов, как то было в эпоху меценатства, когда литература представляла действительно отрасль Lohnwerk’a, но на спрос целого общественного слоя; поэтому меновые отношения между покупателями и продавцами книг теряют характер случайной индивидуальной сделки и становятся типичными или общественными. Наконец, труд, созидающий книгу и определяющий ее меновую ценность, вполне может быть приведен и действительно приводится к абстрактному, всеобщему труду. По отношению к рабочим писчебумажной фабрики, метранпажам и др., созидающим материал книги, это очевидно само собой. Но и труд литераторов не представляет исключения. По мере общественного разделения труда, литераторы все более и более обособляются в особую категорию производителей, все более и более исчезает индивидуальный, случайный характер их труда; в процессе конкуренции выясняется в конце концов количество труда, общественно-необходимого для производства литературного произведения данной категории. Как и большинство непосредственных производителей, в современном обществе литераторы обыкновенно не обладают собственными средствами производства и потому продают не продукт своего труда, а рабочую силу<sup class="footnote-ref"><a href="#fn11" id="fnref11">[11]</a></sup>.</p><p>В таких крупных капиталистических предприятиях, каковы современные газеты и журналы, товарный характер труда литераторов выступает с особенной яркостью: здесь меновая ценность рабочей силы, необходимой для производства литературного произведения определенного качества, строго определена, выражена в деньгах и фигурирует в виде таксы поштучной платы с листа: например, перевод — 20 рублей, оригинальные статьи научного характера — 60 руб., беллетристики — 80 руб. и т. п. Если ценность рабочей силы переводчика равняется X раз умноженной ценности рабочей силы простого, неквалифицированного рабочего, то для авторов беллетристических и научных статей соответственное отношение выразится, очевидно, как 3 X, 4 X и т. д.</p><p>Таким образом, массовый характер фабрикации литературных товаров приводит к тому, что индивидуально-особенный труд различных литераторов приравнивается к абстрактному или простому труду в той же наглядно-осязательной форме, как это имеет место вообще для всех квалифицированных рабочих современной промышленности.</p><p>Переходим к особой категории таких товаров, которые, по-видимому, вовсе не являются в виде материальных вещей.</p><p>Возьмем для примера труд наемного педагога, воплощением которого служить ряд уроков или лекций, оплачиваемых по определенной таксе. Лекция, как овеществление труда учителя, отчуждающего его за определенный денежный эквивалент, очевидно, имеет потребительную ценность не для автора — продавца, но исключительно для слушателей — покупателей. Таким образом, первое условие, позволяющее лекции считать товаром, имеется в наличности. Но не препятствуют ли естественные свойства лекций, как нематериального предмета, осуществлению другой необходимой предпосылки ее товарного бытия? Остается рассмотреть, может ли меновое отношение между лектором и слушателями принять <strong>общественный</strong> характер, допускает ли учительский труд приведение его к простому, абстрактному труду. В развитом капиталистическом обществе наемное учительство представляет специальное занятие целого общественного слоя; о монопольном характере учительского труда не может быть и речи. С этой стороны не существует никаких препятствья к тому, чтобы индивидуальный труд отдельного лектора мог обезличиться, свестись к простому абстрактному труду в процессе конкуренции с сотоварищами по профессии. Для того, чтобы эта конкуренция осуществилась во всей полноте, необходимо только, чтобы каждая лекция была способна к товарному круговороту в такой же степени, как и все другие, циркулирующие на рынке товары. Другими словами, каждая данная лекция должна представлять потребительную ценность не только для непосредственных слушателей, которые фактически вступают в сделку с лектором, но и для всех вообще лиц, предъявляющих на рынке спрос на продукты труда нашего учителя.</p><p>Здесь идеалистический продукт педагога наталкивается, по-видимому, на препятствие, не позволяющее ему стать истинным телом товара. В самом деле, лекция может быть потреблена только в то самое время, когда она произносится. Едва замолк последний звук речи лектора, как произведенный им продукт — лекция исчезнет без остатка, уничтожается ее потребительная ценность для кого бы то ни было, а следовательно — и ее меновая ценность. Вследствие этого, покупатели лекции не способны воспользоваться ею подобно всякому другому товару, потребительная ценность которого так или иначе связана с определенными материальным предметом. Будет ли этим товаром фунта табаку или сочинения Пушкина — безразлично, во всяком случае покупатель, не желающий самостоятельно использовать его потребительную ценность, может в каждый данный момент реализовать меновую ценность путем перепродажи. По-видимому, в этом отношении между материальными и нематериальными предметами потребления лежит целая пропасть. Но это только по-видимому.</p><p>Прежде всего, нетрудно убедиться, что вполне нематериальных предметов потребления, в сущности, нет, да и не может быть. По самому устройству органов чувств, всякое восприятие человеческое является результатом материального импульса, результатом ряда физических толчков. Поэтому невозможно представить себе такую человеческую потребность, которая удовлетворялась бы извне, работою других людей без всякого материального физического воздействия. Потребительная ценность лекции может реализоваться также лишь при помощи известного вещественного носителя, правда, весьма непрочного, а именно — при помощи звуковых волн. Если бы звуковые волны могли неопределенно долгое время сохраняться в данном замкнутом пространстве, не теряя своей интенсивности, или если бы был придуман способ их искусственно консервировать и делать способными к переноске<sup class="footnote-ref"><a href="#fn12" id="fnref12">[12]</a></sup>, то лекция, как предмета потребления, ничем не отличалась бы от книги и, следовательно, к ней были бы приложимы все те рассуждения, который развиты выше. Как бы то ни было, интересующая нас разница между устной лекцией и печатной книгой носит, очевидно, не качественный, а лишь количественный характер, сводится к большей или меньшей прочности того материала, который служит условием проявления потребительной ценности этих двух товаров.</p><p>Мы видим уже, что естественные особенности книги вовсе не являются исключительной принадлежностью предметов удовлетворения умственных потребностей; точно так же лекция в среде других товаров отнюдь не представляет чего-либо необычайного.</p><p>Очень многие предметы потребления отличаются чрезвычайной непрочностью тех вещественных свойств, из которых вытекает их потребительная ценность. Так, например, целый ряд пищевых продуктов, предметов, потребительная ценность которых носит ультра-материальный характер, может быть потреблен только непосредственно после их производства.</p><p>Однако, эта черта не мешает им стать вполне развитыми товарами. Единственная особенность в изготовлении таких скоропортящихся товаров заключается в том, что последняя фаза их производства не может быть отделена от торговли. Современный ресторан является вполне законченной формой общественного разделения труда в сфере производства горячего кушанья: собственно поварская обработка пищевых продуктов и их окончательная продажа потребителям не могут быть разделены на два самостоятельных предприятия и, по необходимости, должны сосредотачиваться в руках одного капиталиста.</p><p>Учителя, содержатели ресторанов и вообще все те производители, которым выпало на долю превращать в товары непрочные потребительные ценности, не могут выносить на рынок свои продукты вполне законченными; они вынуждены постоянно иметь наготове все факторы производства (материалы, орудия и рабочую силу), способные в каждый данный момент вступить во взаимодействие и дать предмет, годный к непосредственному потреблению). Таким образом, меновое хозяйство, в своем непреоборимом стремлении установить единообразные общественные отношения между всеми участниками производства, разрушает все <strong>естественные</strong> препятствия, стоящие на пути превращения тех или других продуктов труда в товары. По отношению к скоропортящимся продуктам это приспособление естественных условий к общественным выражается, как мы только что видели, в том, что товарами, образующимися на рынке, являются не готовые, но, так сказать, <strong>потенциальные</strong> потребительные ценности.</p><p>В этом последнем отношении наиболее яркую иллюстрацию представляет один нематериальный товар, играющий особенно крупную роль в капиталистическом обществе. Товар этот — рабочая сила, имеющая, как известно, не только естественные, но и чрезвычайно важные общественные особенности. Рабочая сила — это кровь капиталистического организма, разносящая к различным органам запасы питательных веществ, необходимых для жизни общественного целого. Но кровь, по справедливому выражению Мефистофеля, «ist ein ganz besondrer Saft».</p><p>Особенность рабочей силы заключается, как известно, в том, что ее потребление есть ничто иное, как сам процесс труда.</p><p>Поэтому рабочая сила не только не теряет меновую ценность в процессе потребления, но, наоборот, <strong>создает</strong> ценность и прибавочную ценность, что резко отделяет ее от других товаров. Но нас интересуют в данном случае не общественные особенности товара — рабочей силы, а его естественные, природные свойства. С этой стороны условия товарного существования рабочей силы и всех остальных вещественных предметов потребления вполне аналогичны. Являясь продуктом труда, воплощенного в материальных предметах — средствах существования, рабочая сила сама по себе отнюдь не материальная вещь — она представляет простую возможность затраты труда, известный запас потенциальной энергии, фигурирующей на рынке в форме товара. При этом нематериальный характер рабочей силы вытекает не из специальных особенностей той общественной потребности, для удовлетворения которой она изготовляется, но как раз наоборот — из отсутствия всякого определенного качества в потребительной ценности рабочей силы, этого всеобщего, универсального товара, в равной степени пригодного к созиданию любого предмета потребления. В капиталистическом обществе рабочая сила оказывается, таким образом, единственным продуктом, меновая ценность которого не нуждается для своего воплощения ни в какой материальной оболочке.</p><p>На ранних стадиях товарного развития вещественным носителем меновой ценности рабочей силы выступает сам человек, который и обращается на рынке в форме товара — раба. Капиталистическое хозяйство освобождает производительную душу раба — рабочую силу — от всяких телесных оков, делает ее товаром в ее собственном, идеальном виде.</p><p>Это обстоятельство ничуть не препятствует рабочей силе принять вполне развитую товарную форму, и учение о заработной плате показывает, что рабочая сила. т. е. потенциальная форма всякой потребительной ценности вообще, с таким же успехом выполняет все товарные функции, как и любая специальная потребительная ценность. Очевидно, что и те продукты труда, которые предназначены для удовлетворения качественно-определенных потребностей, могут фигурировать в товарном обращении не только в законченной, но и в потенциальной форме, раз это требуется их специальными особенностями.</p><h2 id="iii-товарныи-фетишизм" tabindex="-1"><a class="header-anchor" href="#iii-товарныи-фетишизм"><span>III. Товарный фетишизм.</span></a></h2><p>Мы пришли к заключению, что никакие <strong>естественные</strong> свойства продуктов не могут помешать им принять товарную форму. Следовательно, различие труда, как образующего и не образующего ценность, может быть сделано только на основании того общего критерия, который установлен нами для производства вообще. Труд, образующий ценность, представляет только специфически товарную форму производительного труда, поэтому условия его существования совершенно те же, какими определяется производительный характер труда в любой общественной формации: продукт труда имеет меновую ценность во всех тех случаях, когда он необходим для удовлетворения какой-либо потребности товарного общества.</p><p>Так обстоит дело, когда мы рассматриваем вопрос с точки зрения общественного производства. Но совершенно иначе представляется оно с точки зрения отдельного производителя — капиталиста. Для капиталиста меновая ценность или, вернее, рыночная цена<sup class="footnote-ref"><a href="#fn13" id="fnref13">[13]</a></sup> является законом, установленным извне, законом природы, к которому капиталист обязан приспособлять свое производство под опасением разорения. Так как закон этот проявляется только в отношении вещей — товаров, то сама ценность становится в глазах капиталиста <strong>естественным</strong> свойством, присущими товару, как предмету природы.</p><p>«Позднее научное открытие, что продукты труда, поскольку они являются ценностями, представляют лишь вещественное выражение потраченного на их производство труда, составляете эпоху в истории человеческого развития, однако, ничуть не рассеивает иллюзию, вследствие которой общественный характер труда кажется объективными свойством его продуктов. В действительности, только для данной особенной формы производства, для производства товарного, имеет силу тот факт, что специфически общественный характер независимых друг от друга частных работ состоит в их равенстве, как человеческого труда вообще, и проявляется в форме ценности продуктов труда. Между тем с точки зрения человека, всецело захваченного отношениями товарного производства, этот факт, как раньше, так и после указанного открытая, является абсолютным и неизменным совершенно так же, как воздух, в качестве физического тела, нимало не изменился от того, что наука разложила его на составные элементы»<sup class="footnote-ref"><a href="#fn14" id="fnref14">[14]</a></sup>.</p><p>Хотя буржуазная политическая экономия в лице её классических представителей и дошла до определения ценности количеством труда, но сама ценность не могла быть ею понята как <strong>специфически-общественное</strong> отношение товарного производства. Напротив, ценность с точки зрения буржуазных экономистов является объективной формой существования общественного характера труда во все исторические эпохи, остается, следовательно, неизменным естественным свойством вещей, причем дело нисколько не изменяется от того, что это естественное свойство создается человеческим трудом, производящим саму вещь. Но это еще не все. Не только способность труда создавать меновую ценность кажется капиталисту естественным законом природы, но и, наоборот, труд представляется ему создающим меновую ценность лишь постольку, поскольку он направлен на изменение материальных, естественных свойств вещей. Как бы ни была необходима с общественной точки зрения данная затрата труда, она никогда не будет признана капиталистом производительной, если не касается самого вещества продукта, если продукт уже принял те вещественные свойства, которые делают его пригодным к непосредственному потреблению.</p><p>Так, труд перевозки является часто совершенно неизбежными условием для того, чтобы продукт производства мог быть в действительности потреблен. Тем не менее предприниматель всегда склонен считать издержки перевозки чистым убытком, истинными faux frais производства, так как для него условием производительности труда является не общественная, а естественная его необходимость, а перевозка, как известно, не только не вносит ничего созидающего в те естественные свойства продукта, которые делают из него потребительную ценность, но даже нередко оказывает на них самое разрушительное влияние. Именно отсюда вытекает положение классической школы, что производительный или образующий ценность (с буржуазной точки зрения это — простая тавтология) труд непременно должен воплощаться в <strong>материальных</strong> предметах.</p><p>Что такая иллюзия господствует в буржуазной, хотя бы и классической науке, представляется вполне естественным: — иллюзия эта неизбежно вытекает из товарного фетишизма, от которого не может отделаться никакая буржуазная идеология. Гораздо менее понятно каким образом мог впасть в подобную же ошибку Маркс, с такою гениальною проницательностью раскрывший фетишистическую психологию товаропроизводителя.</p><p>Во втором томе «Капитала» в главе «Издержки обращения» труд по сохранению товара и труд перевозки относится к категории непроизводительного, между прочим, на том основании, что он не изменяет продуктов вещественно. «Количество продуктов не увеличивается вследствие их перевозки, точно также произведенные ею изменения их естественных свойств не суть, за известными исключениями, полезное действие, которое имелось бы в виду, но неизбежное зло». Труд, необходимый для сохранения товаров в складах, оказывается также непроизводительным, он представляет также лишь «неизбежное зло», неустранимые «издержки сбережения», «они имеют целью... сбережение ценности, которая находится в товаре, как в изделии, в потребительной ценности: поэтому и может быть сохранена только сбережением самого изделия — потребительной ценности». Другими словами, труд является производительным лишь в том случае, если он производит полезное изменение в <strong>естественных</strong> свойствах продукта. Это — точки зрения товарного фетишизма.</p><p>Мы рассмотрели те общие условия, при наличности которых труд становится производительным в товарном хозяйстве. Но современное общество есть не просто товарное, оно в то же время капиталистическое; современное производство является не только производством меновых ценностей, но также производством прибавочной ценности. Капиталистическую форму могут, очевидно, принять лишь такие отрасли народного производства, в которых труд способен стать источником прибавочной ценности для капиталиста-предпринимателя. «Если позволительно, — говорит Маркс, — взять пример вне сферы материального производства, то школьный учитель является производительным работником лишь тогда, когда он не только обрабатывает детские головы, но обрабатывает также самого себя с целью доставить прибыль предпринимателю. Суть дела остается одна и та же, вложит ли предприниматель свой капитал в школьное предприятие, или в колбасное. Таким образом, в содержание понятия «производительный работник» входит не только отношение между человеческой деятельностью и её полезным результатом, между рабочим и продуктом его работы, но и особое, исторически-возникшее, свойственное данному обществу отношение производства, которое заключается в том, что рабочий является непосредственным орудием возрастания капитала»<sup class="footnote-ref"><a href="#fn15" id="fnref15">[15]</a></sup>.</p><p>В предыдущем было установлено, что никакие естественные особенности отдельных отраслей человеческого труда не могут воспрепятствовать им принять форму производства товаров, раз имеются на лицо общественные предпосылки менового хозяйства. Является вопрос, может ли <strong>всякое</strong> производство товаров стать капиталистическим производством, производство меновой ценности — производством прибавочной ценности?</p><p>Вопрос этот уже давно разрешен экономической наукой и разрешен в положительном смысле.</p><p>В настоящее время не возбуждает спора то положение, что всякое товарное производство неизбежно превращается в производство капиталистическое. Таким образом, капиталистический строй возникает, как необходимый результат естественного развития отношений, господствующих в обществе самостоятельных товаропроизводителей и принимающих в позднейшую эпоху капиталистический характер.</p><p>Правда, процесс этот и в настоящее время еще не вполне закончен! Весьма вероятно даже, что само капиталистическое общество успеет сойти с исторической сцены раньше, чем некоторые отдельные виды общественного производства усвоят себе вполне развитую капиталистическую организацию.</p><p>Однако, это обстоятельство не изменяет дела по существу. В абстрактном анализе приходится оперировать не с фактическим содержащем данного исторического момента, представляющего всегда картину пестрого смешения пережитков прошедшего, развитых форм настоящего и зародышей будущего, — но с идеальным построением, в котором изучаемое общественное явление представлено в совершенно чистом и законченном виде. Материалом для такого построения являются не конкретные факты истории, взятые статически, но их движение, тенденции их исторического развития. Применяя этот прием к данному случаю, рассматривая тенденции развития менового общества, мы должны признать, что капитализм стремится ассимилировать себе и действительно мало-помалу ассимилирует все виды труда, унаследованные им от предшествующей стадии, от простого товарного производства.</p><p>Таким образом, специфически-капиталистические отношения, вытекающие из производства прибавочной ценности, не вносят в условия нашей задачи никаких новых данных. Все то, что было сказано о производительном труде в меновом обществе вообще, справедливо по отношению к капиталистическому обществу.</p><h2 id="iv-естественно-необходимыи-и-общественно-необходимыи-труд-так-называемые-издержки-обращения" tabindex="-1"><a class="header-anchor" href="#iv-естественно-необходимыи-и-общественно-необходимыи-труд-так-называемые-издержки-обращения"><span>IV. Естественно-необходимый и общественно-необходимый труд. Так называемые «издержки обращения».</span></a></h2><p>В главе «Издержки обращения» Маркс не ограничивается указанием на нематериальный характер труда по сбережению товаров, перевозке их и т. п. Он выдвигает еще одно соображение, заставляющее причислить известные виды общественно-необходимого труда к непроизводительным издержкам. Это второе соображение заключается в том, что издержки обращения вытекают только из данной определенной формы общественного производства, а именно из товарной его формы.</p><p>Разделив издержки обращения на три категории: 1) чистые издержки обращения, куда относится торговля, счетоводство и банковое дело, 2) издержки сбережения или труд сохранения товаров и 3) издержки перевозки, Маркс находит во всех этих подразделениях общественного труда одну общую черту. «Общим законом для них будет, что <strong>все издержки обращения, происходящие вследствие преобразования формы товара, не прибавляют к нему ценности</strong>. Капитал, затраченный на такие издержки, принадлежит к faux frais капиталистического производства»<sup class="footnote-ref"><a href="#fn16" id="fnref16">[16]</a></sup>. Однако, условия происхождения разных видов таких издержек неодинаковы, а в некоторых случаях это различие даже весьма существенно. «Есть известная разница, с одной стороны, между расходами, потребными на счетоводство, непроизводительной тратой на это рабочего времени и расходами просто на труд покупки и продажи с другой. Последние проистекают только из определенной общественной формы процесса производства, из того, что это есть процесс производства товаров. Счетоводство же, как контроль и идеальное представление в данный момент всего процесса, становится тем необходимее, чем общественнее становятся его размеры<sup class="footnote-ref"><a href="#fn17" id="fnref17">[17]</a></sup>. Итак, труд торговли относится к категории непроизводительного потому, что является специфической принадлежностью товарной формы производства. Труд счетоводства же не имеет этого специально-товарного характера; он существует в том или другом виде при всякой системе общественного хозяйства и становится тем сложнее и необходимее, чем больше делаются размеры самого общественно-производственного целого. Современное счетоводство есть, поэтому, только товарная форма деятельности, необходимой при всяком общественном устройстве. Если счетоводство считается все-таки отраслью непроизводительного труда, то, очевидно, лишь на том основании, что его существование обусловливается не естественными физиологическими потребностями членов общества, но потребностями, непосредственно вытекающими из общественного характера производства, являющимися, следовательно, уже по самому своему происхождение специфически-общественными.</p><p>Понятие «естественной» и «общественной» необходимости в затрате труда выставляется довольно часто, как критерий для различения производительных видов труда от непроизводительных. Так, например, Лексис в статье, помещенной в Handwörterbuch der Staatswissenschaften, пишет: «Объект производства должен быть <strong>народно-хозяйственным</strong>, т. е. благом, находящимся в области народного хозяйства и не зависящим <strong>от данного экономического порядка</strong>, таким благом, которое осталось бы неизменным и в теоретически мыслимом коммунистическом обществе». Поэтому, по мнению Лексиса, из сферы производства «следует исключить действия, состоящие в передаче благ из одних рук в другие»<sup class="footnote-ref"><a href="#fn18" id="fnref18">[18]</a></sup>.</p><p>Впрочем, Лексис далеко не выдерживает этой основной точки зрения. С обычным эклектизмом Катедер-реформерской школы, он уже на 2-ой странице своей статьи совершенно уничтожает то, что было сказано на 1-ой.</p><p>Так, здесь оказывается, что «производительным следует считать всякий труд, который <strong>каким бы то ни было</strong> способом <strong>содействует</strong> создание известного блага или <strong>повышению</strong> его объективной ценности» (курсив наш). С этой новой точки зрения «транспортный труд» и «торговля», принадлежащие несомненно к категории «передачи благ из одних рук в другие», относятся автором уже к сфере производства.</p><p>Гораздо последовательнее проведен интересующий нас взгляд у Н. Зибера. Зибер принимает точку зрения Маркса, выраженную в цитированных нами выдержках, и формулирует ее гораздо более определенно и ярко, чем сам Маркс. «Говоря вообще, — пишет Зибер, — торговцы и приказчики нужны для распределения богатств лишь в том смысле, как для многоэтажного дома нужны лестницы. Как только прекратится подобная, чисто городская потребность в высокими домах, так немедленно вслед за этим прекратится потребность в лестницах. В приложении к классу торговцев то же соображение показывает, что занятие их только еще <strong>историческое</strong>, подобно существованию высоких домов, тогда как занятие производительного класса вечно»<sup class="footnote-ref"><a href="#fn19" id="fnref19">[19]</a></sup>.</p><p>Здесь уже прямо выступает противопоставление естественных потребностей, как «вечных», общественным, как «историческим» или преходящими.</p><p>Не трудно убедиться, что в основе такого взгляда кроется глубочайшее недоразумение.</p><p>Нет сомнения, что «занятие» производительного класса вечно, в то время как «занятие» класса торговцев весьма и весьма исторично. Но ведь, когда дело идет об определении области «производства», мы имеем дело только с <strong>потребительными</strong> ценностями, а потребительные ценности, поскольку они вообще представляют продукт человеческого труда, создаются не трудом или «занятием» вообще, не абстрактным человеческим трудом, а исключительно <strong>данными конкретными</strong> видами труда. Отвлекаясь от <strong>качественной</strong> стороны труда, мы тем самым отвлекаемся от потребительной ценности произведенных им продуктов. Поэтому вечное существование производительной деятельности вообще отнюдь еще не дает нам права выставлять «естественность» потребностей необходимым условием для того, чтобы труд, удовлетворяющий их, был признан производительным. Аргументами в пользу «естественности», как критерия для классификации производительной деятельности человека, могла бы быть лишь вечность определенных конкретных видов труда, воплощающихся в определенных конкретных видах потребительных ценностей. Но в этом последнем смысле ни одна человеческая потребность не может быть признана вечной.</p><p>Так называемые «естественные» потребности представляют нередко прямой результат общественных условий. Древнеримские патриции, как владельцы и организаторы сельскохозяйственного производства латифундий, имели целый ряд потребностей, обусловленных исключительно этою общественною их ролью и потому чуждых всей остальной массе населения.</p><p>Для удовлетворения таких специально-организаторских потребностей дифференцировался целый общественный класс художников, скульпторов, поэтов, философов, наконец, просто остроумных благёров. Все эти профессии необходимо рассматривать как особые отрасли древнеримского <strong>производства</strong>, потому что «высшие» потребности патрициев, являясь неизбежным результатом общественно-экономических отношений тогдашней эпохи, были столь же необходимы, как и потребность в хлебе. Разумеется, это справедливо лишь постольку, поскольку сами патриции были общественно необходимым слоем древнего Рима. Если бы колонам удалось выработать самостоятельные и самодовлеющие экономические организации вроде древнегерманских марок, то класс патрициев немедленно превратился бы из общественно-необходимого в общественно-излишний, он должен был бы сойти с исторической сцены, а вместе с тем труд, предназначенный для удовлетворения патрицианских потребностей, тотчас утратил бы свой производительный характер; продукты этого труда уже не представляли бы общественных потребительных ценностей и потому никоим образом не могли бы быть причислены к категории общественного производства.</p><p>Но не только «высшие» потребности находятся в неразрывной связи с характером общественной организации. То же самое относится и к чисто-физиологическим потребностям, удовлетворение которых необходимо для простого поддержания жизни в самом узком смысле этого слова.</p><p>Личинки насекомых и многоножки представляют в настоящее время потребительную ценность только для энтомолога; между тем существует организация общественного производства, при которой эти членистоногие занимают видное место в ряду пищевых продуктов. Изобретение крючка из рыбных костей, лука и стрел, наконец, открытие огня — все эти технические усовершенствования, выдвинувшие на первый план мясо позвоночных, могли быть сделаны только на известной стадии общественно-хозяйственного развития, не говоря уже о том, что их фактическое применение требует определенных форм сотрудничества и разделения труда. Хотя потребность в пище является сама по себе естественною и вечною, однако, способы ее удовлетворения весьма изменчивы в зависимости от той или другой общественной организации производства. Предметы, имеющие потребительную ценность пищевых продуктов при данном уровне производительных сил общества, лишаются ее при повышении этого уровня. Еще значительнее разница в тех видах труда, которые направлены на изготовление одних и тех же вещей, удовлетворяющих одни и те же потребности, но в различные хозяйственные эпохи.</p><p>Итак, нельзя разделять естественную и общественную жизнь человека. Социальная организация существует не для удовлетворения некоторых специально-общественных потребностей людей, живущих независимо от общества своею самостоятельною естественною жизнью; наоборот, жизнь человека только и возможна в рамках общества. Даже классический Робинзон на своем необитаемом острове боролся за жизнь не как самодовлеющий индивидуум, а как член вполне определенной, а именно буржуазной общественной организации. Робинзон располагал целым арсеналом орудий и средств труда, созданных не им самим, а теми его соотечественниками, продуктами труда которых он имел возможность воспользоваться, снаряжая свой корабль. Поэтому, по существу дела, Робинзон ни на минуту не оставался изолированным от общественных связей, все время он жил и действовал как часть общественного целого, и только это целое могло снабдить его и действительно снабжало средствами для успешной борьбы за жизнь.</p><p>Если бы от кораблекрушения Робинзон не спас ничего, кроме своего «я», то они вряд ли мог бы просуществовать на острове более 5–6 дней: его самостоятельная жизнь была бы просто агонией. Правда, смерть здесь не наступает с такой стремительностью, с какою обыкновенно умирают органы, отделенные от организма; но это еще, конечно, не основание для того, чтобы Робинзон имел право противопоставлять свое «я», как нечто самодовлеющее, обществу, как внешней среде. Связь между индивидуумом и обществом, с одной стороны, и между органом и организмом — с другой не только качественно однородна, но и наблюдаемые здесь количественные различия далеко не так велики, как это может показаться с первого взгляда. Ведь и лейкоцит, вынутый из человеческой крови и помещенный в так называемый «физиологический раствор», весьма долго сохраняет свою способность к амебоидным движениям; и вряд ли человеческий индивидуум в аналогичных обстоятельствах оказался бы значительно жизнеспособнее лейкоцита.</p><p>Впрочем, мы остановились здесь на примере Робинзона, так сказать — на традиции. В действительности пример этот не представляет решительно никакого научного значения уже по одному тому, что общественная наука имеет дело только с типичными, а отнюдь не с исключительными явлениями. Между тем типичный человек никогда не попадает в новые условия жизненной борьбы в одиночку, но всегда вместе с той социальной группой, к которой он принадлежал и раньше. Само собою понятно, что при этом прежде всего приспособляется к новым условиям производственная организация социального целого, а затем уже изменяются психологические и физиологические свойства индивидуумов, приспособляющихся к своей новой роли в новом строе общественного производства. Поэтому никакие, даже чисто физиологические приспособления к условиям жизненной борьбы не может человек усвоить иначе, как при посредстве изменения общественной организации.</p><p>Отдельный человек решительно во всех сферах своей жизни и деятельности фигурирует лишь, как орган коллективного целого: он представляет собою ξωόν πολιτικον в самом полном и безусловном смысле этого слова. Очевидно при этом, что для общественно-производственного целого совершенно безразлично, является ли данная потребность, существующая в нем, совокупностью личных потребностей членов общества, или же прямо выступает в сознании этих последних, как потребность специфически-общественная, вытекающая исключительно из условий социальной организации. В том и другом случае труд, направленный на удовлетворение потребности, одинаково необходимы для поддержания жизни общества и, следовательно, одинаково производителен.</p><p>Виды труда, составляющие, по Марксу, «издержки обращения», представляют именно такой случай удовлетворения специфически-общественных потребностей, вытекающих из товарной формы производства. Рассмотрим, для примера, издержки перевозки в тот период товарного производства, когда господствующей формой обращения товаров является ярмарочная торговля. С индивидуальной точки зрения отдельного потребителя труд доставления продуктов производства на ярмарку несомненно представляет faux frais производства. По своим естественным свойствам, продукт уже на месте производства вполне соответствует требованиям потребителя; перевозка не только не прибавляет ни одного нового свойства, полезного для потребителя, но до известной степени действует на продукт разрушительно — понижает его качество. Как раз наоборот представляется дело с общественной точки зренья.</p><p>Перевозка продуктов в одно место есть необходимый результат данной общественной организации. Не будучи свезенными на ярмарку, продукты вовсе не могут вступить в процесс обмана, а следовательно — и потребления. Эта <strong>общественная</strong> необходимость играет, очевидно, совершенно ту же роль, как и любая <strong>естественная</strong>, она и является «естественной потребностью» данного общества, данной формы производства. При ярмарочном способе товарного обращения продукт на месте производства, несмотря на всю свою <strong>естественную</strong> пригодность к непосредственному потреблению, фактически не может быть потреблен. <strong>Он не имеет еще потребительной ценности</strong>, как не имеет ее жемчуг, пока он находится на морском дне, хотя по своим вещественным свойствам жемчуг, лежащий на дне моря, и жемчуг, красующийся на груди лоретки, ничем между собою не различаются.</p><p>Если мы признаем производительным труд водолаза, достающего жемчуг, то совершенно на том же основании должны признать производительным труд перевозчиков, доставляющих товары на ярмарку.</p><p>Любопытно, что Маркс совершенно определенно формулирует мысль, которую мы только что развили. «Потребительная ценность предметов осуществляется только потреблением, а перемещение их может стать необходимым для потребления, т. е. <strong>потребление обусловливается при этом дополнительным процессом перевозочной промышленности</strong>»<sup class="footnote-ref"><a href="#fn20" id="fnref20">[20]</a></sup>. Если это так, то почему же дополнительный процесс, имеющий для потребления совершенно то же значение, как и все предшествующие стадии производства, оказывается, не в пример этим последним, только «необходимым злом»?</p><p>На этот вопрос Маркс не дает ответа. Что касается остальных видов «издержек обращения», то к ним в полной мере применимы все те соображения, которые мы только что изложили по отношению к издержкам перевозки. Труд сохранения товаров, торговли, банковского дела является <strong>производительным</strong>, так как он общественно-необходим при товарно-капиталистической системе производства.</p><p>Общественный характер потребностей, вызывающих к жизни «издержки обращения», сам по себе еще не дает основания вычеркнуть эти последние из области производства. Но, может быть, издержки обращения нельзя подвести под категорию производства вследствие каких либо <em>специальных</em> особенностей тех <em>капиталистических</em> отношений, которые обусловливают собою их существование? Такие особенности действительно, по видимому, имеются.</p><p>«Метаморфозы Т — Д и Д — Т суть сделки, происходящие между покупателем и продавцом.... Преобразование требует времени и рабочей силы, но не для созидания ценности, а для перемещения ценности из одной формы в другую, причем обоюдные поползновения присвоить себе при этом добавочное количество ценности отнюдь не изменяет дела»<sup class="footnote-ref"><a href="#fn21" id="fnref21">[21]</a></sup>. В самом деле, равенство X товара А = У товара В не изменяется в пользу В оттого только, что В более долгое время находится в процессе обращения, хотя при этом В поглощает, caeteris paribus, больше труда торговцев и больше «издержек сбережения», чем А. «Когда капиталист превратил свой капитал, затраченный на средства производства и рабочую силу, в продукт, в готовый товар, предназначенный на продажу, а между тем он не идет с рук и залеживается на складах, то в это время происходит не просто только застой в процессе возрастания ценности его капитала: издержки на строения, добавочный труд и пр., требуемые для сохранения этого запаса, составляют положительный убыток. Покупатель, которому бы удалось, наконец, продать, осмеял бы его, если бы он ему сказал: товар мой целые шесть месяцев не мог быть продан и капитал мой не только лежит даром, но сохранение его обошлось мне во столько-то. Tant pis pour vous, тем хуже для вас, сказал бы покупатель. Кроме вас, есть ведь еще другие продавцы, товары которых заготовлены только третьего дня; ваш же товар залежался и, вероятно, время оставило на нем свои следы, поэтому вы должны продать дешевле вашего соперника... Если надо превратить свои изделия в деньги, <em>издержки, вызванные тем, что они закреплены в товарной форме, принадлежат к случайностям его (производителя) личного дела</em> и покупателя товара нисколько не касаются»<sup class="footnote-ref"><a href="#fn22" id="fnref22">[22]</a></sup>.</p><p>Итак, издержки обращения вызываются простой невозможностью обратить в данный момент товар в деньги; сколько бы времени ни пришлось выжидать такой возможности, сколько бы труда ни было затрачено на сохранение товара в течение этого времени, ценность товара не увеличивается ни на волос. Труд, принадлежащий к категории «издержек обращения», не создает ценности. Перед нами, по видимому, встает следующая альтернатива: или признать, что не всякий производительный труд может стать в капиталистическом обществе трудом, образующим ценность, или вовсе отвергнуть установленный в первой главе критерий производительного труда.</p><p>Присмотримся ближе к аргументации Маркса. Труд торговли тратится «не на созидание ценности, но на перемещение ценности из одной формы в другую». При этом «превращение товаров, их движение состоит: 1) вещественно — в обмене разных товаров один на другой, 2) формально — в превращении товара в деньги, в продаже и в превращении денег в товары, в покупке. К этим-то функциям обмена товаров при помощи покупки и продажи сводится функция товарного капитала»<sup class="footnote-ref"><a href="#fn23" id="fnref23">[23]</a></sup>. Очевидно прежде всего, что <em>вещественная</em> и <em>формальная</em> функции товарного капитала различны между собою по существу. Это не две стороны одного и того же явления, а два самостоятельные процесса, не только отличные друг от друга, но даже несравнимые между собою. В самом деле: Т — Д — Т, перемена формы ценности, <em>представляет чисто идеологический процесс, происходящий только в головах товаропроизводителей, а отнюдь не реальную метаморфозу вещей — товаров.</em></p><p>Когда X тонн железа вступают в дружеский акт Т — Д — Т с У килограммами сахара, то при этом не только не происходит никакого пресуществления (превращения — <strong>прим.текст.</strong>) железа в сахар, но и само передвижение этих продуктов далеко не является безусловной необходимостью. Сахар, спокойно лежащий на складе плантатора, может быть на бирже десятки раз обменен на железо, находящееся в руках горнозаводчика.</p><p>Употребляя выражение Маркса, при перемене формы ценности «в движении находится не сама вещь, а право собственности на нее». Спрашивается, может ли циркуляция «права собственности» потребовать приложения хоть одного атома живого, человеческого труда? Никому еще, кажется, не приходила мысль передвигать понятия в голове человека при помощи мускульной силы. Труд поглощается не актом обмена, а теми предварительными манипуляциями, которые рабочие торговых заведений, товарных складов, железных дорог должны произвести над продуктом, для того, чтобы он фактически мог реализовать свою товарную функцию в акте Т — Д — Т.</p><p>Таким образом, в действительности только «вещественные» стороны «функции» торгового капитала впитывают в себя живой человеческий труд, формальная же метаморфоза совершенно не требует никаких «издержек» со стороны купца. Без всякой затраты труда возникает в голове купца и покупателя сознание права собственности на принадлежащие им товары, без всякой затраты труда это право собственности переносится в сознании владельца Т и владельца Д с одного объекта на другой.</p><p>Смешенье таких несравнимых явлений, как «превращение товаров» (т. е. перемена формы ценности) и их «движение» (т. е. перемена места вещами) произошло вследствие замены точного понятия «труд» совершенно неопределенным «функция» товарного капитала, — ошибка совершенно та же, в которую, как мы видели выше, впал Зибер, подменив определенный «труд» производителей их расплывчатым «занятием». Превращенье формы ценности может быть названо «функцией» торгового капитала лишь в том смысле, что купец является одним из двух полюсов, между которыми происходит идеологическое взаимодействие, составляющее содержание акта Т — Д. Но с этой точки зрения превращение товара является в такой же степени функцией покупателя-потребителя, выступающего в том же процессе взаимодействия в роли противоположного полюса, для которого рассматриваемый акт представляется уже как Д — Т и служить только средством реализовать потребительную ценность продукта, сорвав с него товарную оболочку меновой ценности.</p><p>Для купца судьба его товара после превращения последнего в деньги не имеет ни малейшего интереса. Как и всякий капиталист, купец видит свое назначение в добывании прибыли, в обмене товара на сумму большую, чем та, которая была затрачена на его приобретение и сохранение. Поэтому отдельному капиталисту действительно кажется, что весь смысл его «функции» сводится к формальной метаморфозе Т — Д — Т + АД, — метаморфозе, представляющей лишь чудодейственный процесс возрастания количества ценности в сфере обращения, но совершенно бессодержательной с качественной стороны.</p><p>Смешение совершенно несравнимых понятий «обращение ценности» и «передвижение товаров» естественно возникает в голове товаропроизводителя. Так как с его точки зрения продукты обладают естественной способностью к обращению, то меновые отношения людей представляются ему как фактическое передвижение товаров, а обращение ценности кажется лишь простым идеологическим отпечатком, неизменно сопутствующим этому вещественному процессу. Кроме того, пока продукт обрабатывается на фабрике, где он изменяется вещественно, роль труда представляется довольно ясной даже для капиталиста. Но как только продукт вступает в сферу торговли, окончательно усвоив себе те вещественные свойства, который делают его пригодными к непосредственному потреблению, для капиталиста начинается область «неизбежного зла» — непроизводительных торговых издержек. Суть дела, конечно, нисколько не изменяется от того, что торговля из побочного занятия многих становится исключительными занятьем немногих, т. е. делается самостоятельною отраслью промышленнаго труда. Поэтому классическая политическая экономия, представлявшая не защиту интересов отдельных категорий капиталистов, но формулировку основных принципов капиталистической идеологии в самой общей, абстрактной их форме, не причисляла торговли к производительной деятельности, и уже на долю позднейших вульгарных экономистов выпала нелегкая обязанность предложить свои «услуги» для реабилитации непроизводительных с классической точки зрения категорий капитала. Мы снова наталкиваемся, таким образом, на знакомую уже нам идеологию товарного фетишизма.</p><p>Так представляется дело капиталисту, но иначе обстоит оно с точки зрения всего общественного хозяйства.</p><p>В каждом обществе конечным назначением производства является поддержание жизни общественного целого, т. е. процесс потребления. В обществе товарном и товарно-капиталистическом продукты только в том случае могут вступить в процесс потребления, если они уже претерпели предварительно сравнительную метаморфозу в процессе обмена. Следовательно, в товарном обществе труд рабочих торговли, необходимый для того, чтобы продукт мог реализовать свою меновую ценность, eo ipso является необходимым и для реализации потребительной ценности продукта. Все производство в целом определялось нами как деятельность, направленная к употреблению общественных потребностей. По отношению к отдельному продукту, производством мы должны признать всю совокупность манипуляций, общественно-необходимых для того, чтобы предмет природы превратить в предмет потребления. «Общественно-необходимый» здесь означает: необходимый при данном уровне производительных сил, техники, а стало быть, и при данных общественных отношениях, так как эти последние реально неотделимы от производства и представляют лишь его общественную сторону, абстрагированную в познании. В капиталистическом обществе труд передвижения, сохранения товаров, труд торгового счетоводства столь же необходим для превращения предмета природы в предмет потребления, как и все предшествующие стадии производства. Поэтому товар, например, сахар, вступая в область торговли, не есть еще предмет потребления, но только сырой материал торгового производства; труд приказчика колониального магазина совершенно также кристаллизуется в ценности сахара, как в ней уже закристаллизовали раньше труд негра, собирающаго сахарный тростники на плантации. Только впитав в себя труд приказчика, сахар становится, наконец, способен совершить формальную метаморфозу Т — Д (Д — Т с точки зрения покупателя), превращающую его из товара в предмет потребления. Конечно, один торговец может употребить гораздо больше времени и труда при продаже товара, чем другой, но из этого вовсе не следует, что «издержки обращения» принадлежат к случайностям «личного дела» торговца. Неодинаковые издержки обращения в первом и втором случае представляют лишь результат различной производительности труда<sup class="footnote-ref"><a href="#fn24" id="fnref24">[24]</a></sup>.</p><p>Несмотря на эти индивидуальные особенности, в торговле, как и во всякой другой отрасли производства, существует известная объективная мера, известное <em>общественно-необходимое</em> количество труда, определяющее те средние «издержки», которые требуются для того, чтобы данный продукт мог фактически реализовать свою товарную функцию. Это общественно-необходимое рабочее время в качестве регулятора торгового производства выступает с особенной яркостью в процессе конкуренции гигантских универсальных магазинов с мелкими, сравнительно, специальными торговыми предприятиями. Поэтому победоносное «tant pis pour vous» покупателя имеет силу лишь тогда, когда труд, потраченный торговцем на сбережение товара, больше общественно-необходимого, но в этом случае оно применимо решительно ко всякой отрасли производства.</p><p>Роли должны совершенно изменится, если покупатель не пожелает оплатить ценности общественно-необходимого труда торговли, мотивируя свой отказ тем, что ему подобные «издержки» кажутся совершенно непроизводительными: «tant pis pour vous», — скажешь продавец, — «вы не единственный покупатель и, конечно, другие не так наивны, чтобы воображать, что я и мои приказчики стараемся les beaux yeux потребителей».</p><p>Признав непроизводительным труд, занятый в торговле, Маркс должен был ответить на следующий вопрос: каким же образом купец не только возмещает свои непроизводительные «издержки» в ценности продаваемого продукта, но и получает обычную в данном обществе норму прибыли? Вопрос этот исследован в 17-й главе III тома «Капитала». «Товарно-торговый капитал... не создает ни ценностей, ни прибавочных ценностей, но только содействует их реализации, а вместе с тем действительному обмену товаров, переходу их из рук в руки «общественному обмену веществ»<sup class="footnote-ref"><a href="#fn25" id="fnref25">[25]</a></sup>. Тем не менее, как капитал, «играющий самостоятельную роль в процессе обращения», он «должен точно также давать годовую среднюю прибыль, как и те капиталы, которые действуют в различных отраслях производства. Так как сам купеческий капитал ценности не производит, то очевидно, что прибавочная ценность, которая приходится на его долю в виде средней прибыли, <em>образует часть прибавочной ценности, произведенной валовым производительным капиталом</em> (курсив наш). Но вопрос заключается в следующем: каким образом купеческий капитал присваивает часть прибавочной ценности при прибыли, создаваемой производительным капиталом, приходящейся на его долю»<sup class="footnote-ref"><a href="#fn26" id="fnref26">[26]</a></sup>. По видимому, этого результата купец может достигнуть, только делая номинальную надбавку к цене товара и продавая свой товар выше его «цены производства».</p><p>Однако, «представление о происхождении прибыли из номинального повышения цены товаров или вследствие их продажи выше их ценности ведет свое происхождение от поверхностного представления о торговом капитале. Если посмотреть на дело ближе, то окажется, что это простая иллюзия....» <sup class="footnote-ref"><a href="#fn27" id="fnref27">[27]</a></sup>). В действительности, торговый капитал лишь «участвует в прибыли, не принимая участия в ее производстве».</p><p>Приступая к анализу роли торгового капитала в общем производстве всей национальной прибавочной ценности, Маркс разграничивает прежде всего собственно торговые издержки и все другие виды издержек обращения. «Какого бы рода эти издержки обращения ни были, вызываются ли они самим купеческим предприятием, т. е. принадлежат ли они к издержкам обращения, свойственным капиталисту, как купцу; или же они представляют статьи, возникающие вследствие дополнительных процессов производства, происходящих в пределах процесса обращения, как отсылка товаров, перевозка, хранение и пр. и пр.: — все они предполагают, что купцом, кроме денежного капитала, необходимого на покупку товаров, затрачен еще дополнительный капитал — на покупку и оплату этих средств обращения.</p><p>Насколько такой элемент издержек состоит из оборотного капитала, он входит весь в продажную цену товара в виде дополнительного элемента; насколько же он состоит из основного капитала, он входит в нее соразмерно со степенью его изнашивания, но в виде элемента, образующего номинальную ценность, даже если он не образует действительной надбавки к ценности товара, как чисто купеческие издержки обращения. Но весь этот капитал оборотный ли, или основной, входит фактором в образовании общей нормы прибыли» <sup class="footnote-ref"><a href="#fn28" id="fnref28">[28]</a></sup>). В этой выдержке, да и в других местах 17-й главы III тома, бросается в глаза странное противоречие с тем, что было сказано в VI главе II тома. Там все без исключения «издержки обращения» причислялись к faux frais производства, не образующие ценности; здесь оказывается, по видимому, что только чисто купеческие издержки (т. е. труд приказчиков, бухгалтеров, конторщиков и т. п.) «не образуют действительной надбавки к цене товара», тогда как все остальные виды издержек «входят в продажную цену товара в виде дополнительного элемента».</p><p>Очевидно, прежде всего, что различать toto coelo чисто купеческие издержки от издержек «отсылки, перевозки и т. п.» совершенно не представляется возможным. Если труд носильщика, доставляющего товар из склада на полку магазина, может дать надбавку к ценам товара, то почему же труд приказчика, перекладывающего тот же товар с полки на прилавок, перед покупателем, должен быть лишен этого права? Такое «чудо», конечно, не может совершиться вследствие простой перемены лиц. Труд бухгалтера не может, по мнению Маркса, составить элемент цены товара, потому что труд этот тратится не на «созидание» ценности, а на простое «вычисление» уже готовой меновой ценности. Поскольку здесь имеется в виду само содержание математических выкладок бухгалтера и их определяющее влияние на продажную цену товара, то конторские расходы, а в том числе и самый труд вычисления ценности, заносятся, конечно, в графу «издержек», а потому считаются элементом цены. Но Маркса интересует, конечно, не бухгалтерские приемы регистрации, не субъективное воззрение капиталиста на состав цены его товара, а действительная роль конторского труда в образовании этой последней.</p><p>С этой точки зрения труд бухгалтера должен быть признан создающими ценность ничуть не меньше, чем труд любого производительного работника, раз в капиталистическом обществе труд вычисления общественно-необходим для реализации меновой ценности продукта, а следовательно — и его потребительной ценности. Если бы была возможна такая общественная организация, в которой переливание воды из пустого в порожнее являлось бы безусловно необходимым для того, чтобы сделать эту воду фактическим предметом потребления, то даже такое совершенно бессодержательное занятие было бы <strong>производительным</strong> трудом, разумеется, до тех пор, пока существует породивший его общественный строй.</p><p>Итак, если издержки перевозки и хранения «входят в продажную цену товара в качестве дополнительного элемента», то то же самое должно относиться и к «чистым» купеческим издержкам.</p><p>Но что обозначает само выражение «дополнительный элемент цены»? Мы уже видели, что Маркс отнюдь не подразумевает здесь номинальную надбавку цены, производимую самим купцом. <strong>Действительную</strong> надбавку к ценам производства также нельзя предположить, ибо только что было установлено, что «функция обращения» никакой ценности не производит. Если, наконец, издержки обращения входят в цену производства, определяемую издержками + средняя прибыль <strong>промышленного</strong> капитала, то они не только не представляют никакой «прибавки», но как раз наоборот — являются «вычетом» из прибавочной части ценности, произведенной трудом другой категории рабочих. Где же истина?</p><p>«Экспедиционная плата, издержки перевозки, затраты на уплату пошлины и т. п.», — пишет Маркс, — «могут отчасти рассматриваться таким образом, как будто купец затрачивает их на покупку товаров, и потому они входят для него в покупную цену» <sup class="footnote-ref"><a href="#fn29" id="fnref29">[29]</a></sup>).</p><p>Эти неопределенные «могут», «как будто бы», «отчасти», столь не гармонирующие с обычной энергией и категоричностью стиля Маркса, показывает, по видимому, что сам автор чувствовал шаткость своей позиции. В самому деле, здесь, очевидно, нас отсылают только «от Понтия к Пилату», ничуть не разрешая вопроса по существу. Если действительно еще до вступления торгового капитала в отправление своей функции издержки обращения уже были перенесены на цену продукта, то это значит только, что виновником этого перенесения был не купец, а кто-нибудь другой. Если же в приведенных словах излагается только отношение самого купца к издержкам обращения, то тут даже не заключается попытки ответить на интересующий нас вопрос, так как цена продукта определяется не желанием купца, но общественным процессом, «происходящим за его спиной», совершенно не зависящим от его воли. Впрочем, только что цитированное замечание не развивается Марксом; в тексте оно поставлено в скобки, из чего видно, что Маркс бросил его в своей черновой рукописи мимоходом, скорее в форме вопроса, чем в форме решения. Вполне определенно точка зрения Маркса выражена в следующих строках.</p><p>Имея в виду только «такие дополнительные расходы, которые сопряжены с самым предприятием обращения», Маркс говорит: «Если в нашем примере <sup class="footnote-ref"><a href="#fn30" id="fnref30">[30]</a></sup>) кроме 100 купеческого капитала было затрачено еще 50 дополнительного капитала на упомянутые расходы, то валовая прибавочная ценность в 180 распределится уже на производительный капитал в 900 плюс купеческий капитал 150, всего на 1050. Следовательно, средняя норма прибыли понизится до 17¹/₇⁰/₀. Фабрикант продает товары купцу за 900 + 154²/7 = 1054²/₇, а купец продает их за 1130 (1080 + 50 за те расходы, которые ему надо возместить») <sup class="footnote-ref"><a href="#fn31" id="fnref31">[31]</a></sup>). Тут уже не остается сомнения, что издержки обращения составляют <strong>действительную</strong> надбавку к продажной цене продукта, что они возмещаются купцом путем <strong>повышения</strong> цены производства, определяемой средним органическим составом промышленного капитала. В самом деле, если бы «издержки обращения» представляли только <strong>вычет</strong> из валовой прибавочной ценности, созданной трудом промышленных рабочих, то, очевидно, продажная цена продукта была бы не 1130, а по прежнему 1080, прибыль же 130 (180- 50) распределилась бы на капитал — равный 1050, и таким образом, средняя её норма была бы не 17¹/₇⁰/₀, а всего 12²/₇⁰/₀. Но в цену продукта входят только «издержки», употребленные купцом на покупку рабочей силы, а не сам «труд», представляющий реализацию последней. «Так как купец в качестве простого агента (?) не производит ни ценности, ни прибавочной ценности, то и торговые рабочие, занятые им для совершения тех же функций, не могут создать для него непосредственно прибавочной ценности <sup class="footnote-ref"><a href="#fn32" id="fnref32">[32]</a></sup>)». Таким образом, издержки обращения представляют с точки зрения всего общественного капитала просто увеличение постоянной его части; средняя прибыль, причитающаяся на них, составляет вычет из прибавочной ценности промышленного капитала, сами же они только возмещаются в ценности продукта. Появляется вопрос: каким образом происходит такое возмещение, «каким образом он (купец) сохраняет, сберегает эту часть своего постоянного капитала? <sup class="footnote-ref"><a href="#fn33" id="fnref33">[33]</a></sup>). Вопрос этот поставлен Марксом, но не разрешен им <sup class="footnote-ref"><a href="#fn34" id="fnref34">[34]</a></sup>). Нетрудно убедиться, что данный вопрос не только не разрешен, но и <strong>неразрешим</strong> с точки зрения Маркса. Теория трудовой ценности утверждает, что лишь живой человеческий труд, соединяясь с средствами производства, способен «сберегать» ценность постоянной части капитала, лишь этот труд, кристаллизируясь в ценности продукта, возмещает «издержки» переменного капитала, и не просто возмещает их, но дает некоторый плюс в виде прибавочной ценности. В торговле, как оказывается, дело происходит как раз наоборот: труд не создает ценности, а издержки на его оплату каким-то образом сами собой входят в ценность продукта, подобно постоянному капиталу; они возмещаются в цене товара просто потому, что были раз затрачены. Мы вступаем, таким образом, в область буржуазной теории, определяющей ценность издержками, икс — иксом.</p><p>Подробный анализ роли торгового капитала в общем производстве страны не входит в нашу задачу. Вопрос этот должен быть исследован совершенно в другой связи. Поэтому мы не будем утомлять читателя разбором тех аргументов, которым Маркс в главах об «оборотах торгового капитала» и о «денежно-торговом капитале» старается подкрепить свое учение о непроизводительном характере торгового труда, тем более, что по отношению к основному вопросу, поставленному в настоящей статье, мы не найдем здесь ничего существенно нового. Необходимо коснуться еще только одного положения, систематически проводимого Марксом во всех главах III-го тома, трактующих о торговом капитале.</p><p>«…...ничего не может быть бессмысленнее», — пишет Маркс, — «как рассматривать купеческий капитал, в форме ли товарно-торгового, или в форме денежно-торгового капитала, как особого рода промышленный капитал, в виде подразделения, образовавшегося, подобно горному делу, земледелию, скотоводству, мануфактуре, перевозной промышленности и т. д., вследствие общественного разделения труда, а потому составляющая особую отрасль затрат промышленного капитала. Уже простое наблюдение, что <strong>каждый промышленный капитал, пока он находится в фазе обращения процесса своего производства, совершает в виде товарного и денежного капитала совершенно те же функции, который кажутся принадлежащими исключительно к функциям торгового капитала</strong> в обеих его формах, должно бы сделать такое представление невозможным» <sup class="footnote-ref"><a href="#fn35" id="fnref35">[35]</a></sup>). (Курсив наш).</p><p>Таким образом, торговля не представляет какой-либо самостоятельной отрасли промышленного труда, торговый капитал есть лишь фаза, момент в процессе воспроизводства промышленного капитала. В основе этой мысли лежит то же недоразумение, которое уже было подробно разобрано нами выше; именно, предполагается, что «вещественная» функция торгового капитала, функция, заключающаяся в передвижении: товаров и конторской <sup class="footnote-ref"><a href="#fn36" id="fnref36">[36]</a></sup>) деятельности, представляет затрату труда, расходуемого на «формальную» метаморфозу ценности. В действительности только формальная метаморфоза и составляет фазу обращения в процессе производства промышленного капитала, но эта метаморфоза сама по себе не поглощает ни одного атома труда. Поскольку же «функция» купца требует приложения живого человеческого труда, она составляет не фазу обращения, а фазу <strong>производства</strong>, является производительной, а потому рассматривать торговлю, как «подразделение, образовавшееся подобно горному делу, земледелию, скотоводству, мануфактуре и т. п.» вовсе не так «бессмысленно», как это может показаться с первого взгляда.</p><p>Итак, не фактически наблюдаемые особенности заставляют выделить труд торговли, перевозки и сбережения товаров из сферы производства, но как раз наоборот — a priori признанная непроизводительность указанных видов труда заставляет придать им при анализе некоторые своеобразные особенности, заставляет создать фикцию «дележа прибавочной ценности», так как без этой фикции оказалось бы совершенно непонятным, каким образом труд, образующий ценность, может быть в то же время непроизводительным.</p><p>Несколько особую роль играют те «издержки обращения», которые необходимы не для циркуляции товаров, а для циркуляции денег. Хотя для отдельного предпринимателя товар фигурирует лишь как товарная форма его капитала, как вещественный носитель самовозрастающей ценности, однако, с общественно-производственной точки зрения целью существования товара на рынке оказывается просто заключительной фазой производства его, как потребительной ценности. Не так обстоит дело с деньгами. Общественная потребительная ценность денег состоит в реализации их функции всеобщего эквивалента, в их роли необходимого промежуточного звена в метаморфозе Т—Д—Т¹. Поэтому процесс циркуляции денег есть в то же время процесс их потребления. Из самих условий этого своеобразного потребления вытекает необходимость, чтобы фактически перемещающиеся денежные знаки неопределенно долгое время сохраняли неизменную меновую ценность. Труд банковских служащих, обеспечивающий непрерывность процесса потребления денег в качестве всеобщего эквивалента, очевидно, не может воплощаться в ценности самих денег; между тем, удовлетворяя общественную потребность, необходимо вытекающую из организации капиталистического строя, труд этот должен быть признан производительным. То обстоятельство, что труд банковских служащих не создает никакой материальной вещи, которую бы можно было передавать из рук в руки, не представляет уже для нас затруднения. Во всяком случае, банковые служащее являются истинными <strong>производителями</strong> капиталистического общества, продукт их работы есть настоящий товар, продаваемый через посредство банкиров в руки капиталистов <sup class="footnote-ref"><a href="#fn37" id="fnref37">[37]</a></sup>).</p><p>Этот товар действительно оплачивается из прибавочной ценности капиталиста, но не потому, что произведший его труд непроизводителен, а потому, что этот труд воплощается в предметах потребления класса капиталистов. В этом отношении общественно-производственные роли банкира и золотых дел мастера совершенно одинаковы <sup class="footnote-ref"><a href="#fn38" id="fnref38">[38]</a></sup>).</p><p>Специальная особенность предметов потребления, изготовляемых банкиром, та, что здесь непосредственно-общественная потребность становится как бы личною потребностью отдельных капиталистов. В самом деле, деньги, как всеобщий эквивалент, представляют вещественное выражение общественного характера труда отдельных, независимых друг от друга производителей. Следовательно, потребность в том, чтобы деньги фактически фигурировали в роли всеобщего эквивалента, является специфически-общественной; между тем на практике она выступает как потребность отдельных капиталистов, удовлетворяющих ее продуктами банковского производства. Таким образом, капиталист является здесь не человеком, обладающим определенными естественными свойствами и потребностями, но простой материализацией данного общественного отношения, живым воплощением капитала. Впрочем, это вовсе не исключительный случай. Для капиталиста цель производства заключается не в потреблении, не в удовлетворении личных потребностей, наоборот ― личное потребление есть в его глазах лишь неизбежное зло, такая же печальная необходимость, как и всякие другие непроизводительные издержки, ограничивающие процесс воспроизведения капитала в расширенных размерах. В капиталическом обществе не только способы удовлетворения личных потребностей обусловлены организацией, но даже сами эти личные потребности принимают в глазах предпринимателя непосредственно общественный характер. Капиталист становится простой машиной, приводящей в движение капитальную ценность. Власть общественных отношений над людьми достигает своего апогея. Мне нет надобности дальше останавливаться на этом вопросе, так как читатель, конечно, помнит те блестящие страницы первого тома «Капитала», где с поразительною яркостью развертывается психология капиталиста, отражающая в себе тенденцию капитальной ценности к непрерывному самовозрастанию.</p><p>К двум указанным выше типам возможно, как нам кажется, свести все виды «издержек обращения». Одни из них представляют производительный труд, воплощающийся в нематериальных предметах потребления капиталистов, другая прямо входят в меновую ценность известных материальных предметов потребления и только кажутся непроизводительными, хотя и неизбежными издержками.</p><h2 id="v-общественно-необходимыи-труд-и-общественныи-паразитизм" tabindex="-1"><a class="header-anchor" href="#v-общественно-необходимыи-труд-и-общественныи-паразитизм"><span>V. Общественно-необходимый труд и общественный паразитизм.</span></a></h2><p>Как мы старались показать, соображения, изложенные Марксом в VI главе II тома, не требуют никаких существенных поправок в сделанном нами общем определении производительного труда; мы считаем также единственно правильным — рассматривать труд, образующий ценность, просто, как капиталистическую форму производительного труда вообще.</p><p>Если величина меновой ценности товара определяется <strong>количеством</strong> труда, общественно-необходимого для его производства, то само существование товара, как меновой ценности, возможно только в том случае, если труд, употребленный на его производство, общественно-необходим <strong>качественно</strong>. Необходимые при данных общественных условиях и, в то же время, непроизводительные и не образующее ценность виды труда представляют с этой точки зрения non sens.</p><p>В первой главе настоящей статьи был взят совершенно случайный, исключительный пример непроизводительного труда. Между тем, непроизводительный труд может иметь не только случайный, но и общественный характер. Последнее имеет место главным образом тогда, когда развитие производительных сил общества оставляет за флагом известный социальный слой, делает его из общественно-необходимого общественно-излишним или паразитическими. Примером нам может послужить мелкое немецкое рыцарство XIV и XV веков. Развитие товарного производства имело, как известно, своим результатом разрушение экономической самостоятельности мелких поместий; самодовлеющие общественный ячейки натурального хозяйства средних веков стали тесны для новых производственных форм, мелкое землевладение уступает место крупному, а общая организаторская власть сосредоточивается в руках князей, ставших во главе нового общества — государства, окончательно сложившегося только в XVI, XVII столетиях.</p><p>Мелкое рыцарство, утратив материальные условия своего существования в качестве организаторов, делает своею специальностью грабеж на торговых дорогах. Каждый рыцарский бург становится вполне самостоятельным обществом, для которого весь окружающий мир со всеми его обитателями, включая сюда и людей, представляется просто внешней <strong>природой</strong>; поэтому разбойничий промысел с точки зрения бурга является не только трудом, но и <strong>производительным</strong> трудом, — это просто охотничье хозяйство с той только небольшою особенностью, что предметом охоты являются здесь не зайцы или бекасы, а купцы. Наоборот, с точки зрения нового товарного общества, рыцари-разбойники представляются, конечно, чисто паразитическими элементами, слоем не только общественно-излишними, но и общественно-вредным. Здесь паразитизм приобретает своеобразную форму государства в государстве; отживающие общественные элементы составляют самостоятельную общественную организацию, вырабатывают систему планомерной деятельности, направленной к высасыванию соков из развивающегося общественного организма.</p><p>Впрочем, общественный паразитизм не всегда принимает вид непроизводительного труда; наиболее часто представляется просто бездеятельными потреблением. Французское дворянство прошлого века дает иллюстрацию паразитического существования этого второго рода. Утратив свою хозяйственную функцию, оно сохранило доходы, вытекавшие когда-то из него, просто вследствие переживания некоторых юридических надстроек. Таким образом, здесь источником существования был не труд, хотя бы и непроизводительный. Что касается собственно деятельности, то во всех подобных случаях она не имеет никакого отношения к производству средств существования и носит исключительно <strong>потребительный</strong> характер; объективным её результатом является, как и в животном мире, лишь гипертрофия функций питания и размножения, — впрочем, у человека неприглядная картина обыкновенно несколько скрашивается непомерными развитием воображения и вытекающих отсюда эстетических эмоций.</p><p>Итак, отвлекаясь от совершенно случайных, индивидуальных видов непроизводительного труда, мы считаем возможным противопоставлять общественно-производительные функции только общественному паразитизму, причем последний представляет два вида. В одних случаях деятельность паразитического слоя общества носит строго потребительный характер; существование такого слоя обеспечивается не трудом. Если бы французский дворянин прошлого столетия, вместо того, чтобы блистать на балах, заниматься охотой, придворными интригами и философией, вздумал пролежать всю жизнь на диване, то это не внесло бы никакого потрясения в его материальную обеспеченность, его доходы не уменьшились бы от этого ни на один экю.</p><p>В других случаях необходимым условием существования паразитического класса является труд, но труд отнюдь не вытекающий из производственного строя данного общества, но, наоборот, разрушающий этот строй, утилизирующий его в паразитическом смысле. Это наблюдается тогда, когда исчезает не только производительная функция данного класса, но и те юридические формы, которые были когда то её «надстройкой», её непосредственным отражением.</p><p>Разбойничий рыцарь не может отказаться от своей «вредной» деятельности. Для него прекратить охоту на купцов — значит умереть.</p><p>В заключение нам остается коснуться еще одного вопроса, а именно: чем же объясняются неточности и ошибки, так странно пестрящие главу об «издержках обращения» во II томе и главы о купеческом капитале в III т. труда Маркса? Довольно элементарные противоречия в исследовании «издержек обращения» поражают прямо недоумением читателя, находящегося под обаянием поразительной силы и тонкости анализа сложнейших вопросов, разбираемых автором в других местах сочинения. Было бы, разумеется, нелепостью объяснять эти противоречия логической несостоятельностью писателя, и это тем более, что у него же мы почти всегда находим ключ к разрешению недоразумений. Настоящее изложение, в сущности, представляет только попытку развить некоторые замечания Маркса, разбросанные им и в VI главе II тома, и в других местах его трактата.</p><p>Причину ошибок надо искать не в устройстве логического аппарата автора, но в устройстве того общества, которое окружало его в эпоху составления «Капитала». К сожалению, мы здесь не имеем ни малейшей возможности сколько нибудь обстоятельно исследовать этот вопрос, но суть его разрешения, как нам кажется, заключается в следующем.</p><p>Признав такие виды труда, как торговля, банковское дело и проч., производительными, Маркс тем самым вынужден был бы признать их и общественно-необходимыми. А в этом случай пришлось бы или вовсе отказаться от той перспективы, которая развертывается на известных страницах главы «о первоначальном накоплении», или указать общественную тенденцию, <strong>мало-помалу</strong> превращающую некоторые категории производительных работников капиталистического строя из общественно-необходимого элемента в общественно-излишний. Такая тенденция предполагает, очевидно, не только появление так называемых «материальных условий» новой общественной организации, но и фактическое развитие самой этой организации, постепенное образование новых форм общественно-производственной деятельности, внедряющих в старые отношения и вытесняющих их шаг за шагом, так что последние принимают в конце концов характер чисто юридических пережитков..</p><p>В переходные эпохи, когда материальные предпосылки нового общества находятся еще «в процессе зарождения», немыслимо дать полную и законченную формулировку законов этой едва возникающей эволюции, немыслимо также вполне правильно оценить значение тех сторон «старого порядка», роль которых в процессе общественного новообразования еще не выяснена самою жизнью. Пробелы, остающиеся, таким образом, в анализе, по необходимости, заполняются представлениями старой, отжившей идеологии.</p><p>Ex nihilo, как известно, nihil fit, из ничего нельзя создать даже идеи. Поэтому какова бы ни была умственная сила гениального исследователя, она может проявиться только в той или другой комбинации существующего материала; результата её не творчество, которое вообще невозможно, а лишь формировка (формирование — <strong>прим.текст.</strong>) того, что уже есть.</p><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 143. <a href="#fnref1" class="footnote-backref">↩︎</a></p></li><li id="fn2" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 144, примечание 7. <a href="#fnref2" class="footnote-backref">↩︎</a></p></li><li id="fn3" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 473. <a href="#fnref3" class="footnote-backref">↩︎</a></p></li><li id="fn4" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 473. <a href="#fnref4" class="footnote-backref">↩︎</a></p></li><li id="fn5" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 142. <a href="#fnref5" class="footnote-backref">↩︎</a></p></li><li id="fn6" class="footnote-item"><p>Такое недоразумение составляет характеристическую особенность большинства учебников и руководств, многочисленных лер-бухов, ханд-бухов и просто бухов политической или «национальной» экономии. Почтенные составители считают своей неизменной обязанностью для большей полноты включить в свою программу и потребление. Совершенно с таким же правом можно бы для большей полноты включить в учение о мышцах курс хореографического искусства, в технологию бумажного производства — критический обзор литературы. <a href="#fnref6" class="footnote-backref">↩︎</a></p></li><li id="fn7" class="footnote-item"><p>Das Kapital, I B. Vierte Aufl., S. 1. <a href="#fnref7" class="footnote-backref">↩︎</a></p></li><li id="fn8" class="footnote-item"><p>Форма X товара А=Y товара В взята здесь только для большей общности. Двусторонний ее характер, выражающийся в том, что А для владельца В и В для владельца А представляют зародышевую форму всеобщего эквивалента, не имеет для нас значения. Рассматривая только процесс с точки зрения А, мы можем просто считать B вполне дифференцировавшимся всеобщим эквивалентом. <a href="#fnref8" class="footnote-backref">↩︎</a></p></li><li id="fn9" class="footnote-item"><p>Маркс &quot;Критика основных положений политической экономии&quot;, стр. 21. <a href="#fnref9" class="footnote-backref">↩︎</a></p></li><li id="fn10" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 41. <a href="#fnref10" class="footnote-backref">↩︎</a></p></li><li id="fn11" class="footnote-item"><p>По форме отчуждения рабочей силы, большинство современных литературных предприятий относится к так называемой «домашней системе капиталистической промышленности» (Hausindustrie). <a href="#fnref11" class="footnote-backref">↩︎</a></p></li><li id="fn12" class="footnote-item"><p>Такой способ, как известно, существует со времени изобретения фонографа. <a href="#fnref12" class="footnote-backref">↩︎</a></p></li><li id="fn13" class="footnote-item"><p>Дальше средних рыночных цен анализ буржуазной экономии, как известно, никогда не мог проникнуть. <a href="#fnref13" class="footnote-backref">↩︎</a></p></li><li id="fn14" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S.40–41 <a href="#fnref14" class="footnote-backref">↩︎</a></p></li><li id="fn15" class="footnote-item"><p>Das Kapital, I B. Vierte Auflage, S. 473 <a href="#fnref15" class="footnote-backref">↩︎</a></p></li><li id="fn16" class="footnote-item"><p>Капитал, т. II, стр. 97. <a href="#fnref16" class="footnote-backref">↩︎</a></p></li><li id="fn17" class="footnote-item"><p>Капитал, т. II, стр. 86. <a href="#fnref17" class="footnote-backref">↩︎</a></p></li><li id="fn18" class="footnote-item"><p>Цитируем по русскому переводу, изд. Водовозовой, Сборник «Промышленность», стр. 1. <a href="#fnref18" class="footnote-backref">↩︎</a></p></li><li id="fn19" class="footnote-item"><p>Давид Рикардо и Карл Маркс в их «общественно-экономических исследованиях». Стр. 27, 3-е издание. <a href="#fnref19" class="footnote-backref">↩︎</a></p></li><li id="fn20" class="footnote-item"><p>Капитал, т. II. стр. 98. <a href="#fnref20" class="footnote-backref">↩︎</a></p></li><li id="fn21" class="footnote-item"><p>Капитал, т. II, Стр. 82. <a href="#fnref21" class="footnote-backref">↩︎</a></p></li><li id="fn22" class="footnote-item"><p>Капитал, т. II. Стр. 82 и 83. <a href="#fnref22" class="footnote-backref">↩︎</a></p></li><li id="fn23" class="footnote-item"><p>Капитал т. III. Стр. 260. <a href="#fnref23" class="footnote-backref">↩︎</a></p></li><li id="fn24" class="footnote-item"><p>В данном случае техническое совершенство производства определяется наиболее целесообразным приспособлением его не к естественным условиям производства, а к тем формам рыночных отношений и рыночной конкуренции, которые существуют в данном обществе. Нам нет надобности повторять, что это нисколько не изменяет сути дела. <a href="#fnref24" class="footnote-backref">↩︎</a></p></li><li id="fn25" class="footnote-item"><p>Капитал. т. III. Стр. 222. <a href="#fnref25" class="footnote-backref">↩︎</a></p></li><li id="fn26" class="footnote-item"><p>Капитал. т. III. Стр. 222. <a href="#fnref26" class="footnote-backref">↩︎</a></p></li><li id="fn27" class="footnote-item"><p>Капитал. т. III Стр. 224. <a href="#fnref27" class="footnote-backref">↩︎</a></p></li><li id="fn28" class="footnote-item"><p>Капитал. т. III Стр. 227—228. <a href="#fnref28" class="footnote-backref">↩︎</a></p></li><li id="fn29" class="footnote-item"><p>Капитал, т. III, Стр. 228. <a href="#fnref29" class="footnote-backref">↩︎</a></p></li><li id="fn30" class="footnote-item"><p>Пример этот следующий: Весь промышленный капитал страны, затраченный в течении года = 720с + 180v = 900, а m¹ (норма прибавочной ценности) = 100%. Следовательно, продукт 720с + 180v + 180m = 1080. Норма прибыли на весь капитал 180100900 = 20%. Предположим теперь, что к этим 900 промышленного капитала присоединяются еще 100 купеческого капитала, который, с точки зрения Маркса, сам прибыли не создает, но получает среднюю норму прибыли сообразно своей величине. «Издержки обращения» принимаются равными нулю. Очевидно, при этих условиях прежние 180m распределяются уже не между 900 всего капитала, а между 900 + 100 = 1000. Норма прибыли будет 1801001000 =18%. См. Капитал. т. III. <a href="#fnref30" class="footnote-backref">↩︎</a></p></li><li id="fn31" class="footnote-item"><p>Капитал. т. III, Стр. 231. <a href="#fnref31" class="footnote-backref">↩︎</a></p></li><li id="fn32" class="footnote-item"><p>Капитал. т. III, Стр. 232. <a href="#fnref32" class="footnote-backref">↩︎</a></p></li><li id="fn33" class="footnote-item"><p>Капитал. т. III. Стр. 232 <a href="#fnref33" class="footnote-backref">↩︎</a></p></li><li id="fn34" class="footnote-item"><p>II и III томы Капитала остались, как известно, не законченными. Многое в них только намечено, и это прежде всего относится к интересующим нас в данный момент вопросам; они только мельком сформулированы Марксом, очевидно, с целью более подробно заняться ими при окончательной обработке рукописи. Если бы такая обработка состоялась, то, конечно, многие существующие теперь противоречия были бы устранены. Как бы то ни было, в настоящее время приходится иметь дело с тем, что нам дал Маркс, а не с тем, что он мог бы дать, если бы преждевременная смерть не подкосила его в самом разгаре научной работы. Это тем более справедливо, что большинство последователей Маркса принимают оставшееся от учителя наследство целиком, в том незаконченном виде, в каком оно дошло до нас. <a href="#fnref34" class="footnote-backref">↩︎</a></p></li><li id="fn35" class="footnote-item"><p>Капитал. т. III. Стр. 257—258. <a href="#fnref35" class="footnote-backref">↩︎</a></p></li><li id="fn36" class="footnote-item"><p>Как было показано во II главе, характер конторской деятельности ничем по существу не отличается от тех видов труда, которые изменяют само вещество продукта. <a href="#fnref36" class="footnote-backref">↩︎</a></p></li><li id="fn37" class="footnote-item"><p>Разумеется, прибыль банкира отнюдь не соответствует неоплаченному труду его собственных служащих. В этом отношении банковское точно так же, как и торговое производство, подлежит действию тех же общих законов, которые исследованы Марксом в 1-ом отделе III-го тома «Капитала». <a href="#fnref37" class="footnote-backref">↩︎</a></p></li><li id="fn38" class="footnote-item"><p>Условия реализации меновой ценности разменных продуктов капиталистического производства в зависимости от того, потребностям каких классов общества они удовлетворяют, не имеют ничего общего с вопросом о производительном или непроизводительном характере данного вида труда. Анализ этих условий представляет в 3-ем отделе II тома «Капитала». См. также книжку Булгакова «О рынке при капиталистическом производстве» и последние главы диссертации Т. Барановского «Промышленные кризисы в Англии». <a href="#fnref38" class="footnote-backref">↩︎</a></p></li></ol></section>',166))])}const I=p(g,[["render",u]]),m=JSON.parse('{"path":"/%D1%82%D1%80%D1%83%D0%B4_%D0%BF%D1%80%D0%BE%D0%B8%D0%B7%D0%B2%D0%BE%D0%B4%D0%B8%D1%82%D0%B5%D0%BB%D1%8C%D0%BD%D1%8B%D0%B9_%D0%B8_%D1%82%D1%80%D1%83%D0%B4_%D0%BE%D0%B1%D1%80%D0%B0%D0%B7%D1%83%D1%8E%D1%89%D0%B8%D0%B9_%D1%86%D0%B5%D0%BD%D0%BD%D0%BE%D1%81%D1%82%D1%8C.html","title":"Труд производительный и труд, образующий ценность","lang":"ru-RU","frontmatter":{"format":"comtext","version":"2.0.0","title":"Труд производительный и труд, образующий ценность","author":["Владимир Александрович Базаров"],"lang":"ru-RU"},"git":{},"filePathRelative":"труд_производительный_и_труд_образующий_ценность.md"}');export{I as comp,m as data};
